vec3 rgb2yuv(vec3 rgb)
{
	vec3 yuv;
	yuv.x = dot(rgb, vec3(0.299, 0.587, 0.114));
	yuv.y = dot(rgb, vec3(-0.14713, -0.28886, 0.436));
	yuv.z = dot(rgb, vec3(0.615, -0.51499, -0.10001));
	return yuv;
}

vec3 yuv2rgb(vec3 yuv)
{
	vec3 rgb;
	rgb.r = yuv.x + yuv.z * 1.13983;
	rgb.g = yuv.x + dot(vec2(-0.39465, -0.58060), yuv.yz);
	rgb.b = yuv.x + yuv.y * 2.03211;
	return rgb;
}

#define _Frequency 1.0
#define _TimeX 1.0
#define _Amount 0.45
#define _Offset 2.35
#define _LinesWidth 1.0
#define _Alpha 1.0
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

		float TimeX = _TimeX*iTime*0.5;
		float strength = 0.0;
		//strength = 10.0;
		strength = 0.5 + 0.5 * cos(TimeX * _Frequency);

		TimeX *= strength; 

		//	[1] 生成随机强度梯度线条
		float truncTime = trunc(TimeX, 4.0);
		float uv_trunc = randomNoise(trunc(uv.yy, vec2(8, 8)) + 100.0 * truncTime);
		float uv_randomTrunc = 6.0 * trunc(TimeX, 24.0 * uv_trunc);

		// [2] 生成随机非均匀宽度线条
		float blockLine_random = 0.5 * randomNoise(trunc(uv.yy + uv_randomTrunc, vec2(8.0 * _LinesWidth, 8.0 * _LinesWidth)));
		blockLine_random += 0.5 * randomNoise(trunc(uv.yy + uv_randomTrunc, vec2(7, 7)));
		blockLine_random = blockLine_random * 2.0 - 1.0;
		blockLine_random = sign(blockLine_random) * clamp((abs(blockLine_random) - _Amount) / (0.4),0.0,1.0);
		blockLine_random = mix(0.0, blockLine_random, _Offset);


		// [3] 生成源色调的blockLine Glitch
		vec2 uv_blockLine = uv;
		uv_blockLine = clamp(uv_blockLine + vec2(0.1 * blockLine_random, 0.0),0.0,1.0);
		vec4 blockLineColor = texture(iChannel0, abs(uv_blockLine));

		// [4] 将RGB转到YUV空间，并做色调偏移
		// RGB -> YUV
		vec3 blockLineColor_yuv = rgb2yuv(blockLineColor.rgb);
		// adjust Chrominance | 色度
		blockLineColor_yuv.y /= 1.0 - 3.0 * abs(blockLine_random) * clamp(0.5 - blockLine_random,0.0,1.0);
		// adjust Chroma | 浓度
		blockLineColor_yuv.z += 0.125 * blockLine_random * clamp(blockLine_random - 0.5,0.0,1.0);
		vec3 blockLineColor_rgb = yuv2rgb(blockLineColor_yuv);


	// [5] 与源场景图进行混合
	vec4 sceneColor = texture(iChannel0, uv);
    // Output to screen
    fragColor = mix(sceneColor, vec4(blockLineColor_rgb, blockLineColor.a), _Alpha);
}

