float _Indensity = 0.01;
float _Amplitude = 1.5;
float _Amount = 0.3;
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float splitAmout = (1.0 + sin(iTime * 6.0)) * 0.5;
    splitAmout *= 1.0 + sin(iTime * 16.0) * 0.5;
    splitAmout *= 1.0 + sin(iTime * 19.0) * 0.5;
    splitAmout *= 1.0 + sin(iTime * 27.0) * 0.5;
    splitAmout = pow(splitAmout, _Amplitude);
    splitAmout *= (0.05 * _Amount);

    vec3 finalColor;
    finalColor.r = texture(iChannel0, vec2(uv.x + splitAmout, uv.y)).r;
    finalColor.g = texture(iChannel0, uv).g;
    finalColor.b = texture(iChannel0, vec2(uv.x - splitAmout, uv.y)).b;

    finalColor *= (1.0 - splitAmout * 0.5);

    // Output to screen
    fragColor = vec4(finalColor,1.0);
}

// noise function
float randomNoise(float x, float y)
{
    return fract(sin(dot(vec2(x, y), vec2(12.9898, 78.233))) * 43758.5453);
}

float _Indensity = 0.01; 
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float splitAmount = _Indensity * randomNoise(iTime, 2.0);
    // rgb split
    vec4 ColorR = texture(iChannel0, vec2(uv.x + splitAmount, uv.y));
    vec4 ColorG = texture(iChannel0, uv);
    vec4 ColorB = texture(iChannel0, vec2(uv.x - splitAmount, uv.y));

    // Output to screen
    fragColor = vec4(ColorR.r, ColorG.g, ColorB.b,1.0);
}

#define RINNER 0.62
#define ROUTER 0.65
#define REPITITION 32.0

const float PI = 3.14159265359;
const float TWOPI = 6.28318530718;

vec2 complexPow(vec2 z, float n) {
    float r = length(z);
    float angle = atan(z.y, z.x);
    return pow(r, n) * vec2(cos(n * angle), sin(n * angle));
}

mat2 rotate2d(float angle){
    return mat2(cos(angle),-sin(angle),
                sin(angle),cos(angle));
}

float sdCircle(vec2 p, float r)
{
    return length(p) - r;
}

float sdModulatedCircle(vec2 p, float radius)
{
    float repeat = REPITITION;

    // Circle is centered at origin, convert to polar coordinates
    float r = length(p);
    float angle = atan(p.y, p.x);

    float bump = smoothstep(0.25, 1.25, cos(angle * repeat + iTime) + 1.0) * 0.125;

    return r - (radius + bump);
}

float sdSubtract(float d1, float d2 ) {
    return max(-d1,d2);
}

float sdCombine( float d1, float d2 ) {
    return min(d1,d2);
}

float gear(vec2 p) {
	float d1 = sdCircle(p, RINNER);
	float d2 = sdModulatedCircle(p, ROUTER);

    return sdSubtract(d1, d2);
}

float ring(vec2 p) {
	float d1 = sdCircle(p, RINNER - 0.1);
    return abs(d1) - 0.15;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized/centered pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) /iResolution.y;
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    float zoom  = 4.0;
    vec2 p = uv * zoom;

    // Animate complex inversion
    float t = iTime * 0.25;
    float inv1 = -(clamp(mod(t, 2.0), 0.0, 1.0) - 0.5) * 2.0;
    float inv2 = -(clamp(mod(t, 2.0) - 1.0, 0.0, 1.0) - 0.5)  * 2.0;
    float rotation = (1.0 - step(mod(t, 2.0), 1.0)) * PI;

    p = rotate2d(rotation) * p;
    p = complexPow(complexPow(p, inv1), inv2);

    float d = gear(p);
    d = sdCombine(d, ring(p));

    // SDF Coloring based on https://www.shadertoy.com/view/3ltSW2

    float sgn = sign(d);

    //col = col - sgn * vec3(0.1,0.4,0.7);
    //col *= 1.0 - exp(-20.0 * abs(d));
	//col *= 0.8 + 0.2 * smoothstep(0.0, 2.0, cos(50.0*d) + 1.0);
	//col = mix( col, vec3(1.0), 1.0-smoothstep(0.008,0.02,abs(d)) );

    if (sgn > 0.0) {
        col *= 1. - smoothstep(d, 0.85, 1.0);
    }

    col += smoothstep(d, 0.55, 1.0);


    // Output to screen
    fragColor = vec4(col,1.0);
}

