const int AA = 2;
#else
const int AA = 4;
#endif

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float stime = mod( iTime, 6.0 );

    vec3 col = vec3(0.0);
    for( int j=0; j<AA; j++ )
    for( int i=0; i<AA; i++ )
    {
        vec2 of = vec2(i,j)/float(AA);
        vec2 p = (2.0*(fragCoord+of)-iResolution.xy)/iResolution.y;

        p *= 1.6;
        //p.y += 0.5;

        float di = float( hash(ivec2(fragCoord)*AA+ivec2(i,j))&255 )/255.0;
        float time = stime + ((float(j*AA+i)+di)/float(AA*AA))*(0.5/30.0) - 0.5*0.5/30.0;

        time += 0.07*sin( p.y );

        float wing = (p.x<0.0)?position_bad(time, 2.0):
                               position_good(time, 2.0);

        if( p.x>0.0 ) p.x=p.x-1.4;
        else          p.x=p.x+1.4;


        float an = 1.55*(0.5-0.5*cos(2.0*6.283185*wing));
        vec2  pl = vec2(sign(p.x)*sin(an),-cos(an));

        vec3 ro = vec3(0.0,0.085,2.1);
        vec3 rd = vec3(p,-3.0);
        vec3 pos = ro - rd*dot(ro.xz,pl)/dot(rd.xz,pl);
        vec2 q = vec2( length(pos.xz), pos.y );

        vec4 tmp = butterfly( q );
        tmp = clamp(tmp,0.0,1.0);
        tmp.xyz *= 0.1+0.9*mix(1.0,abs(q.y)*0.5+min(q.x*2.0,1.0),pl.x*pl.x);
        tmp.xyz *= clamp(0.25+0.75*(pl.x-pl.y+1.0),0.0,1.0);


        col += mix( vec3(0.5), tmp.xyz, tmp.w );
    }
    col /= float(AA*AA);

    vec2 q = fragCoord/iResolution.xy;
    col += sin(fragCoord.x*114.0)*sin(fragCoord.y*211.1)/512.0;

    fragColor = vec4(col,1.0);
}

#define ITERS 12
const float scale = 2.;
const float scale1 = 1.15;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(0.0);
    vec3 col=vec3(0.0),col_prev=vec3(0.0);
    vec2 uv = (fragCoord*10.0-iResolution.xy)/iResolution.y/15.0;
    //uv.y += (iTime)/25.0;
    float s1 = scale1*scale+uv.y+iTime/4.0;
    for(int c=0;c<ITERS;c++){
        col_prev = col;
        for(int i=0;i<ITERS;i++)
        {
            uv= -fract(-uv-((vec2(uv.x/scale-uv.y/scale1,uv.y/scale-uv.x/scale1)/(scale))))/scale1;
            uv.x *= -scale1;
            uv = fract(uv.yx/s1)*s1;
            uv.y /= scale1;
        }
        col[2] = abs(fract(uv.y)-fract(uv.x));
        col = ((col+col_prev.yzx))/2.125;
	}
    fragColor = vec4(vec3(col*3.0),1.0);

}

/*
This turned out pretty clean, simple, and understandable! Also it never evaluates
more than four typically expensive functions (e.g. trig, square roots, etc.)
which is one up on my other cubic solvers.

I also added handling for when p=0 which simply blows up the whole thing but I'm not
checking for degenerate cubic equations (please don't solve linear equations
with this, ok? ;))
*/

// Special cube root to fix GLSL's negative pow() bug
float cbrt(in float x) { return sign(x) * pow(abs(x), 1.0 / 3.0); }
int solveCubic(in float a, in float b, in float c, in float d, out vec3 roots) {
    float u = b / (3.0 * a);

    // Depress to x^3 + px + q by substituting x-b/3a
    // This can be found by substituting x+u and solving for u so that the x^2
    // term gets eliminated (then of course dividing by the leading coefficient)
    float p = (c - b * u) / a;
    float q = (d - (c - 2.0 * b * b / (9.0 * a)) * u) / a;

    // Everything blows up when p=0 so give this case special treatment
    if (abs(p) < 1e-9) { roots.x = cbrt(-q) - u; return 1; }

    // In the case of one root, this construction does not work
    float h = 0.25 * q * q + p * p * p / 27.0;
    if (h > 0.0) { // Check depressed cubic discriminant
        h = sqrt(h);
        float o = -0.5 * q;
        roots.x = cbrt(o - h) + cbrt(o + h) - u; // Cardano's formula (see https://en.wikipedia.org/wiki/Cubic_equation)
        return 1;
    }

    // Solve by mapping an inverse smoothstep between the critical points
    // I found a whole lot simplified right out so now it probably looks rather obfuscated
    float m = sqrt(-p / 3.0);
    roots.x = -2.0 * m * sin(asin(1.5 * q / (p * m)) / 3.0);

    // Factor out the root to solve for the rest as a quadratic
    h = sqrt(-3.0 * roots.x * roots.x - 4.0 * p);
    roots.yz = 0.5 * vec2(h - roots.x, -h - roots.x);
    roots -= u; // Undo the change in variable

    return 3;
}

