void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y * 4.0;
    float unit = 8.0 / iResolution.y;
    vec3 color = vec3(0.0);

    // Animated coefficients
    float a = sin(iTime) * 2.0;
    float b = sin(iTime) * 5.0;
    float c = cos(iTime * 0.75);
    float d = sin(iTime * 0.5);

    float f = a * uv.x * uv.x * uv.x + b * uv.x * uv.x + c * uv.x + d;
    float g = 3.0 * a * uv.x * uv.x + 2.0 * b * uv.x + c; // Derivative for distance estimate
    float cubic = abs(uv.y - f) / sqrt(1.0 + g * g); // Distance estimate

    color = mix(color, vec3(0.0, 0.0, 1.0), smoothstep(unit, 0.0, abs(uv.y))); // X axis
    color = mix(color, vec3(1.0, 0.0, 0.0), smoothstep(unit, 0.0, cubic)); // Cubic function

    // Solve for the roots of the derivative (a quadratic)
    // to get the extrema
    float da = 3.0 * a;
    float db = 2.0 * b;
    float dc = c;

    float discr = db * db - 4.0 * da * dc;
    vec2 ex1 = vec2(0.0), ex2 = vec2(0.0);
    if (discr > 0.0) {
        discr = sqrt(discr);
        da *= 2.0;

        // Extrema 1
        ex1 = vec2((-db + discr) / da, 0.0);
        ex1.y = ((a * ex1.x + b) * ex1.x + c) * ex1.x + d;

        // Extrema 2
        ex2 = vec2((-db - discr) / da, 0.0);
        ex2.y = ((a * ex2.x + b) * ex2.x + c) * ex2.x + d;

        // Draw the segment between the extrema where the inverse smoothstep
        // gets fitted
        if (min(ex1.x, ex2.x) < uv.x && uv.x < max(ex1.x, ex2.x)) {
            color = mix(color, vec3(1.0, 0.8, 0.0), smoothstep(unit, 0.0, cubic - 0.025));
        }

        // Draw the extrema
        color = mix(color, vec3(1.0, 0.0, 1.0), smoothstep(unit, 0.0, length(uv - ex1) - 0.05));
        color = mix(color, vec3(1.0, 0.0, 1.0), smoothstep(unit, 0.0, length(uv - ex2) - 0.05));
    }

    // Draw roots
    vec3 roots;
    int nroots = solveCubic(a, b, c, d, roots);
    for (int n=0; n < nroots; n++) {
        vec3 rootColor = vec3(1.0);
        if (n == 0 && sign(ex1.y) != sign(ex2.y)) rootColor.rb = vec2(0.0); // Draw inverse smoothstepped root in green
        color = mix(color, rootColor, smoothstep(unit, 0.0, length(uv - vec2(roots[n], 0.0)) - 0.05));
    }

    fragColor = vec4(color, 1.0);
}

