vec5 depth( vec3 origin, vec3 direction )
{
    float depth = 0.0;
    float light = 0.0;
    float iterations = 0.0;
    vec2 closestPoint = vec2(cameraFarPlane, 0);


    vec3 b = direction/length(direction);//b = normalized direction
    vec3 p = origin; //point or direction vector times depth

    float d = cameraFarPlane; //Distance

    float didCollide = 1.0;

    for(float i=0.;i<maxSteps;i++)
    {
        p = origin + (b*depth);
        d = DF(p).x;

        iterations++;

        if (d < closestPoint.x) closestPoint.x = d;

        if (d < minDistance || depth > cameraFarPlane)
        {
            if ((d > minDistance))
            {
                didCollide = 0.0;
            }

            break;
        }

        depth += d;

    }

    if (depth < cameraFarPlane)
    {
        light = evaluateLight(p);
    }


    depth /= cameraFarPlane;


    return vec5(vec4(depth, light, iterations/maxSteps, closestPoint.x), didCollide);
}

vec2 circle( vec2 centrePos, float radius, float deg)
{
  float x = centrePos.x + radius * cos(deg * pi / 180.0);
  float y = centrePos.y + radius * sin(deg * pi / 180.0);
  return vec2(x, y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord/iResolution.xy) * 2.0 - 1.0;
    uv.x *= iResolution.x / iResolution.y;

    vec2 mouse = iMouse.z>0.?(iMouse.xy/iResolution.xy) * 2.0 - 1.0:vec2(0);
    mouse.x *= iResolution.x / iResolution.y;
    mouse *= mouseSensitivity*fieldOfView;    


    vec2 dirY = circle(vec2(0, 0), 1.0, mouse.y + uv.y *fieldOfView);
    vec2 dirXY = circle(vec2(0, 0), dirY.x, mouse.x*5.0 + uv.x*fieldOfView);
    vec3 dirV = vec3(dirXY.y, dirY.y, dirXY.x);//Polar Coordinate Spherical Projection 
    //I know this introduces distortion but I came up with this technique right when I was getting into shaders
    //so I'm rather fond of it and decided to keep it :)


    vec4 pixelInfo =depth(cameraCoords, dirV).a;

    vec3 lightCol = /*debugCol(0.0);*/lightColor * pixelInfo.y * light;
    vec3 glowCol = glowColor * pixelInfo.z * glow;
    vec3 col = lightCol + glowCol;


    fragColor = vec4(col.xyz, 1.0);
}

