void mainImage( out vec4 O, vec2 U )
{
    O.r = float( T(vec2(U.x, 8))*R.y > U.y ); // display curve in row  8
    O.g = .7*float( T(vec2(U.x,16))*R.y > U.y ); // display curve in row 16
    O.b = 0.;

    if ( abs(U.y-T(vec2(U.x,24))*R.y) <1. ) O.r += .5;  // display max
    if ( abs(U.y-T(vec2(U.x,32))*R.y)< 1. ) O.g += .5;

if ( int(U.y) == int(R.y)/2 ) O.r+O.g > 1. ? O-- : O++; // y=0 axis
}

