#define BIASED_NORMAL 	1
#define MAX_DIST		150.
const float PI = radians(180.);



float map(vec3 p)
{
	return length(p) - 1.0;
}



float rayMarch(in vec3 ro, in vec3 rd, in float offT)
{
  float t = offT;
  for (int i = 0; i < 40; i++)
  {
    float h = map(ro + rd * t);
    t += h;
    if (h < 0.001 || t > MAX_DIST)
        break;
  }
  return t;
}

#define EPS  0.01

#if BIASED_NORMAL
vec3 calcNormal(vec3 pos)
{
    float	ref;
    float	trans;
    vec3	absorb;
    vec3	col;
    vec2	eps = vec2(EPS, 0);
	float d = map(pos);
	return normalize(vec3(map(pos + eps.xyy) - d, map(pos + eps.yxy) - d, map(pos + eps.yyx) - d));
}
#else
vec3 calcNormal( in vec3 pos )
{
    const float ep = EPS;
    vec2 e = vec2(1.0,-1.0)*0.5773;
    return normalize( e.xyy*map( pos + e.xyy*ep ) + 
					  e.yyx*map( pos + e.yyx*ep ) + 
					  e.yxy*map( pos + e.yxy*ep ) + 
					  e.xxx*map( pos + e.xxx*ep ) );
}
#endif

vec3 applyFog(vec3 rgb,float distance, vec3 fogColor)
{
    float fogAmount = 1.0 - exp( -distance*0.2 );
    return mix( rgb, fogColor, fogAmount );
}


vec3 Render(in vec3 ro, in vec3 rd)
{
    float t = rayMarch(ro, rd, 0.);
    vec3	col = vec3(0);
    if(t < MAX_DIST)
    {
        vec3 p = ro + rd * t;
        vec3 n = calcNormal(p);   
        col = texture(iChannel0, reflect(rd, n)).rgb;
    }
    return col;
}

mat3 setCamera( in vec3 ro, in vec3 ta )
{
	vec3 cw = normalize(ta-ro);
	vec3 up = vec3(0, 1, 0);
	vec3 cu = normalize( cross(cw,up) );
	vec3 cv = normalize( cross(cu,cw) );
    return mat3( cu, cv, cw );
}

void mainVR( out vec4 fragColor, in vec2 fragCoord, in vec3 ro, in vec3 rd )
{
    fragColor = vec4(Render(ro + vec3(0, 0, 9), rd), 1);
}


