// ##### Random Number Generation #####

// Hash44 Hash without Sine: https://www.shadertoy.com/view/4djSRW
vec4 hash44(vec3 pos, float t)
{
    vec4 p4 = vec4(pos, t);
    p4  = fract(p4*vec4(0.1031, 0.1030, 0.0973, 0.1099));
    p4 += dot(p4, p4.wzxy+33.33);
    return fract((p4.xxyz+p4.yzzw)*p4.zywx);
}

// PCG Random: https://www.pcg-random.org/
uint hash(uint x)
{
    uint state = x*0x2C9277B5U+0xAC564B05U;
    uint word  = ((state >> ((state >> 28U) + 4U)) ^ state) * 0x108EF2D9U;
    return (word >> 22U) ^ word;
}

//
uint ns;
#define initializeRNG ns = uint(frame)*uint(resolution.x*resolution.y)+uint(gl_FragCoord.x+gl_FragCoord.y*resolution.x)

//
float rand()
{
    ns = hash(ns);
    return float(ns)/float(0xFFFFFFFFU);
}

// Random Vectors
vec2 rand2(){return vec2(rand (), rand());}
vec3 rand3(){return vec3(rand2(), rand());}
vec4 rand4(){return vec4(rand3(), rand());}
//vec5 rand5(){return vec5(rand4(), rand());} // GLSL Sus Imposter Confirmed!? The truth is out there...

// See michael0884's usage of PCG Random
// https://www.shadertoy.com/view/wltcRS
// https://www.shadertoy.com/view/WttyWX

// Normalized Random Vectors
// A point is equally probable to land at every point inside the volume of a sphere at the mean with a radius of sigma
vec2 nrand2(float sigma, vec2 mean)
{
    vec2 z = rand2();
    return mean+sigma*sqrt(-2.0*log(z.x   ))*vec2(cos(2.0*pi*z.y), sin(2.0*pi*z.y));
}

vec3 nrand3(float sigma, vec3 mean)
{
    vec4 z = rand4();
    return mean+sigma*sqrt(-2.0*log(z.xxy ))*vec3(cos(2.0*pi*z.z), sin(2.0*pi*z.z), cos(2.0*pi*z.w));
}

vec4 nrand4(float sigma, vec4 mean)
{
    vec4 z = rand4();
    return mean+sigma*sqrt(-2.0*log(z.xxyy))*vec4(cos(2.0*pi*z.z), sin(2.0*pi*z.z), cos(2.0*pi*z.w), sin(2.0*pi*z.w));
}

// Uniform Random Vectors
// A point is equally probable to land at every point on the surface of a sphere with a radius of 1.0
vec3 urand3()
{
    vec2 z = rand2();
    vec2 r = vec2(2.0*pi*z.x, acos(2.0*z.y-1.0));
    vec2 s = sin(r), c = cos(r);
    return vec3(c.x*s.y, s.x*s.y, c.y);
}

