//
float getDensity(in vec3 pos, out vec3 volumeColor, out vec3 emission)
{
    // Set-Up Variables
    volumeColor = vec3(0.20, 0.15, 0.10);
    emission = vec3(0.0);

    // Bounds (increases performance by skipping sampling noise where it isn't needed)
    if(dotp(pos.xz) > 8.0 || abs(pos.y) > 0.3)
    {
        return 0.0;
    }

    // Accretion Disk 
    vec3 gasColor = blackbodyXYZ(2300.0);
    gasColor = XYZtoRGB(gasColor);
    gasColor = clamp(gasColor/max(max(gasColor.x, gasColor.y), gasColor.z), 0.0, 1.0);

    // Noise
    float volumeNoise = fbm(vec3(rotate2(pos.xz, pos.y+length(pos.xz)*2.0), pos.y).xzy*20.0);

    // Emission
    emission = gasColor*128.0*max(volumeNoise-length(vec3(0.2, 8.0, 0.2)*pos), 0.0)/((dotp(pos*0.5)*dotp(pos*0.5))+0.05);

    // Return Density
    return max(volumeNoise-length(vec3(0.12, 7.5, 0.12)*pos), 0.0)*128.0;
}

// Color of the Sky
vec3 skyColor(vec3 dir)
{
    // Pure Black Void
    return vec3(0.0);
}

// Path-Tracing
vec3 radiance(vec3 ro, vec3 rd)
{
    // Set-Up Variables
    vec3 raypos = ro+(rand()*rd*dt);
    vec3 velocity = rd;
    vec3 attenuation = vec3(1.0);
    vec3 radiance = vec3(0.0);
    vec3 volumeColor = vec3(1.0);
    vec3 volumeEmission = vec3(0.0);
    uint bounces = 0U;

    // Ray-Marching
    for(uint i = 0U; i < maxSteps; i++)
    {
        // Check if we reached the bounce limit
        if(bounces > maxBounces)
        {
            break;
        }

        //if(length(raypos) > maxDist)
        if(dotp(raypos) > maxDist*maxDist)
        {
            radiance += attenuation*skyColor(normalize(velocity));
            return radiance;
        }

        // Get the Volume at the Ray Position
        float density = getDensity(raypos, volumeColor, volumeEmission);

        // Compute the Absorbance of the Volume
        float absorbance = exp(-1.0*density*dt);

        // Emission
        radiance += attenuation*volumeEmission*dt;

        // Determine if the Ray will Bounce
        if(absorbance < rand())
        {
            // Compute the Direction of the Ray
            velocity = reflect(velocity, urand3());
            //velocity = reflect(normalize(velocity), urand3())*length(velocity);

            // Absorbed Light
            attenuation = clamp(attenuation*volumeColor, 0.0, 1.0);

            // Increment the Bounces Counter
            bounces++;
        }

        // Compute the Gravitational Force Acting on the Ray
        vec3 g = gravitationalForce(raypos);

        // Apply the Gravitational Force
        velocity += g*dt;

        // Redshift/Blueshift?
        //radiance = shiftColor(radiance, raypos);

        // March the Ray Forward
        raypos += velocity*dt;

        //if(length(raypos) < blackholeRadius)
        if(dotp(raypos) < blackholeRadius*blackholeRadius)
        {
            return radiance;
        }
    }

    // Return Expired Sample
    return vec3(-1.0);
}

// Monte-Carlo Dithering/Anti-Aliasing
vec2 dither(vec2 pos)
{
    return nrand2(0.5, pos);
}

// Render and Output the Frame
void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    // Progressive Rendering
    fragColor = frame != 0 ? texel0 : vec4(0.0);

    // Initialize the Random Number Generator
    initializeRNG;

    // Bloom
    float t = rand();
    if(t < 0.25)
    {
        // Large Bloom
        float r2 = rand();
        fragCoord += nrand2(32.0*r2*r2*r2, vec2(0.0));
    }
    else if(t < 0.5)
    {
        // Microbloom
        fragCoord += nrand2(4.0, vec2(0.0));
    }

    // Screen UV Coordinates
    vec2 uv = 2.0*(dither(fragCoord.xy)-0.5*resolution.xy)/max(resolution.x, resolution.y);

    // Position of the Camera
    vec3 cameraPosition = vec3(0.0, 0.2, -3.5);
    //vec3 cameraPosition = vec3(0.0, 0.4, -3.0);

    // No DoF
    vec3 ro = cameraPosition;
    // Crappy Depth of Field (more infinitely far objects in focus)
