#define pi 3.14159265
#define iterations 13

vec3 function(vec3 x){
    vec3 ca = vec3(0.722,0.918,1.000)*cos(x.xxx)+vec3(1.000,0.902,0.859)*cos(x.yyy);
    vec3 cb = vec3(0.898,1.000,0.922)*cos(x.zzz*1.5);
    return ca*cb-cb;
}

mat2 ROT(float ang)
{
    return mat2(cos(ang), sin(ang), -sin(ang), cos(ang));
}

#define SCALE 1.4
#define SHIFT vec3(2.3, -5.2, 1.0)

vec3 fractal(vec3 x){
    x *= pi;
    vec3 v = vec3(0.0);
    float d = length(x);
    x*=16.0;
    float a = 1.5;
    mat2 rmZ = SCALE*ROT(0.9);
    mat2 rmX = SCALE*ROT(0.46);
    for (int i = 0; i < iterations; i++){
        vec3 F =function(x); 
        v += a*F;
        x.xy = rmZ*x.xy;
        x += 0.6*(F + F.zxy);
        x.yz = rmX*x.yz;
        x += SHIFT;
        a /= 1.05;
    }
    return 50.*v*v*exp(-1000.*max(d - 3.0, 0.0));
}

vec2 map( in vec3 p )
{
    return vec2(length(fractal(p)),0.0);
}

vec3 normal(vec3 p) 
{
    const float dx = 0.0012;
	const vec3 k = vec3(1,-1,0);
	return  normalize(k.xyy*map(p + k.xyy*dx).x +
                      k.yyx*map(p + k.yyx*dx).x +
                      k.yxy*map(p + k.yxy*dx).x +
                      k.xxx*map(p + k.xxx*dx).x + 0.0001);
}

bool trace(inout vec4 ro, vec3 rd)
{
    for(int i = 0; i < MAX_STEPS; i++)
    {
        float de = abs(map(ro.xyz).x); 
        float md = max(CAM_ANGLE*ro.w,MIN_DIST);
        ro += vec4(rd, 1.0)*(de - 2.0*step(de, md)*md); 
        if(de < md) return true;
        if(ro.w > MAX_DIST) return false;
    }
    return true;
}

float pow2(float x)
