const float aperture_size = 0.;
vec2 aperture()
{
    vec2 r = rand2();
    return vec2(sin(TWO_PI*r.x), cos(TWO_PI*r.x))*sqrt(r.y);
}

bool getRay(vec2 uv, out vec3 ro, out vec3 rd)
{
    mat3 cam = getCam(get(CamA));

    vec2 apert_cent = -0.*uv;

    vec2 ap = aperture();

    if(!(distance(ap, apert_cent) < 1.0)) return false;

    float apd = length(ap);

    vec3 daperture = ap.x*cam[0] + ap.y*cam[1];

    ro = get(CamP).xyz + aperture_size*daperture;

    float focus =2.5 + 0.8*pow(apd,5.0);


    rd = normalize(focus*(cam*vec3(FOV*uv, 1.0)) - aperture_size*daperture);

    return true;
}

vec4 render(vec2 fragCoord)
{
    fragCoord += rand2();
    vec2 uv = (fragCoord - 0.5*iResolution.xy)/iResolution.y;

    vec3 cp, rd;
    if(!getRay(uv, cp, rd)) return vec4(0,0,0,1);

    vec4 ro = vec4(cp,0.0);
    vec3 fcol = vec3(0.0);
    vec3 op = vec3(0.0);

    const float dt0 = 0.001;
    ro += vec4(rd,1.0)*dt0*rand();
    for(int i = 0; i < 96; i++)
    {
        vec3 d = fractal(ro.xyz);

        float dt = dt0 + 0.02*ro.w;
        ro += vec4(rd,1.0)*dt;
        op += d*dt;

        vec3 shadow = exp2(-transmittance(ro.xyz, light));
        vec4 cam = atmo(cp, rd, ro.w);
        vec3 inc = shadow;
        vec3 scattering = (d + scatter(ro.xyz, rd, light))*inc;
        vec3 vis = cam.xyz*exp(-op);
        fcol += vis*scattering*dt;
    }

    return vec4(fcol, 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    rng_initialize(fragCoord, iFrame);
    init();

    //prev 
    if(get(CamP).w == 0.0)
        fragColor = texture(iChannel0, fragCoord/iResolution.xy);
    else
        fragColor = vec4(0.0);

   for(int i = 0; i < SPP; i++)
       fragColor += render(fragCoord);
}

