/*
Distance to a weighted "rational" quadratic bezier which
is pretty much a parametrized conic (unweighted quadratic beziers
form parabolas, a special case). It requires solving a quartic equation.

I didn't know about this generalization until I saw this shader
also showing the distance to a rational bezier by @kajbostrom:
https://www.shadertoy.com/view/stjGRh

I learned a bit more about them from demofox's webpage: http://demofox.org/bezquadrational.html
I'm working on simplifying and trying to increase the precision currently.

Btw, the calculations turned out to have lots of patterns!
Desmos graph of the workthrough: https://www.desmos.com/calculator/d68abzjzi9

Python implementation: https://replit.com/@hathnoname/Euclidean-Distance-to-Weighted-Quadratic-Bezier#main.py
*/

// a, b, c: vec3(x, y, weight)
float dot2(in vec2 v) { return dot(v, v); }
float cbrt(in float x) { return sign(x) * pow(abs(x), 1.0 / 3.0); }
float sdWeightedBezier(in vec2 p, in vec3 a, in vec3 b, in vec3 c) {
    // Weighted vertex positions
    vec2 wa = a.xy * a.z, wb = b.xy * b.z, wc = c.xy * c.z;

    // Conversion to power basis makes it less messy
    vec2 p2 = wa - 2.0 * wb + wc, p1 = 2.0 * (wb - wa), p0 = wa;
    float w2 = a.z - 2.0 * b.z + c.z, w1 = 2.0 * (b.z - a.z), w0 = a.z;

    // Reused calculations
    float  pp0 = dot(p,  p0),  pp1 = dot(p,  p1),  pp2 = dot(p,  p2);
    float p0p0 = dot(p0, p0), p1p1 = dot(p1, p1), p2p2 = dot(p2, p2);
    float p0p1 = dot(p0, p1), p0p2 = dot(p0, p2), p1p2 = dot(p1, p2);

    float w0w0 = w0 * w0, w1w1 = w1 * w1, w2w2 = w2 * w2;
    float w0w1 = w0 * w1, w0w2 = w0 * w2, w1w2 = w1 * w2;

    // Quartic coefficients (t4*t^4 + t3*t^3 + t2*t^2 + t1*t + t0)
    float t4 = w1 * p2p2 - w2 * p1p2 + w2w2 * pp1 - w1w2 * pp2;
    float t3 = 2.0 * (w0 * p2p2 - w2 * p0p2  + w2w2 * pp0 - w0w2 * pp2) + w1w2 * pp1 - w1w1 * pp2 + w1 * p1p2 - w2 * p1p1;
    float t2 = 3.0 * (w0 * p1p2 - w2 * p0p1) + 2.0 * (w1w2 * pp0 - w0w1 * pp2) + w0w2 * pp1 - w0w1 * pp2 + w1w2 * pp0 - w0w2 * pp1;
    float t1 = 2.0 * (w0 * p0p2 - w2 * p0p0  + w0w2 * pp0 - w0w0 * pp2) + w1w1 * pp0 - w0w1 * pp1 + w0 * p1p1 - w1 * p0p1;
    float t0 = w0 * p0p1 - w1 * p0p0 + w0w1 * pp0 - w0w0 * pp1;

    // Solve and minimize
    float d = min(dot2(p - a.xy), dot2(p - c.xy)); // Minimize at the boundaries (bezier end points)

    t3 /= t4; t2 /= t4; t1 /= t4; t0 /= t4; // Divide by leading coefficient to make it 1

    // Depress the quartic to t^4 + pt^2 + qt + r by substituting t-b/4a
    // This can be found by substituting t+u and the solving for the value
    // of u that makes the t^3 term go away
    float t3t3 = t3 * t3;
    float dp = (8.0 * t2 - 3.0 * t3t3) / 8.0;
    float dq = (8.0 * t1 - 4.0 * t2 * t3 + t3t3 * t3) / 8.0;
    float dr = (256.0 * t0 - 64.0 * t1 * t3 + 16.0 * t2 * t3t3 - 3.0 * t3t3 * t3t3) / 256.0;

    // Solve for a root to (x^2)^3 + 2p(x^2)^2 + (p^2 - 4r)(x^2) - q^2 which resolves the
    // system of equations relating the product of two quadratics to the depressed quartic
    float ra =  2.0 * dp;
    float rb =  dp * dp - 4.0 * dr;
    float rc = -dq * dq;

    // Depress using the method above
    float ru = ra / 3.0;
    float rp = rb - ra * ru;
    float rq = rc - (rb - 2.0 * ra * ra / 9.0) * ru;

    float lambda;
    float rh = 0.25 * rq * rq + rp * rp * rp / 27.0;
    if (rh > 0.0) { // Use Cardano's formula in the case of one real root
        rh = sqrt(rh);
        float ro = -0.5 * rq;
        lambda = cbrt(ro - rh) + cbrt(ro + rh) - ru;
    }

    else { // Use complex arithmetic in the case of three real roots
        float rm = sqrt(-rp / 3.0);
        lambda = -2.0 * rm * sin(asin(1.5 * rq / (rp * rm)) / 3.0) - ru;
    }

    // Solve two quadratics factored from the quartic using the cubic root
    float x = sqrt(lambda); // Because we solved for x^2 but want x
    float alpha = 2.0 * dq / x, beta = lambda + ra;

    float u = 0.25 * t3;
    x *= 0.5;

    float z = -alpha - beta;
    if (z > 0.0) {
        z = sqrt(z) * 0.5;
        float h = +x - u;
        vec2 t = vec2(h + z, h - z);
        if (t.x > 0.0 && t.x < 1.0) d = min(d, dot2(p - ((p2 * t.x + p1) * t.x + p0) / ((w2 * t.x + w1) * t.x + w0)));
        if (t.y > 0.0 && t.y < 1.0) d = min(d, dot2(p - ((p2 * t.y + p1) * t.y + p0) / ((w2 * t.y + w1) * t.y + w0)));
    }

    float w = +alpha - beta;
    if (w > 0.0) {
        w = sqrt(w) * 0.5;
        float h = -x - u;
        vec2 t = vec2(h + w, h - w);
        if (t.x > 0.0 && t.x < 1.0) d = min(d, dot2(p - ((p2 * t.x + p1) * t.x + p0) / ((w2 * t.x + w1) * t.x + w0)));
        if (t.y > 0.0 && t.y < 1.0) d = min(d, dot2(p - ((p2 * t.y + p1) * t.y + p0) / ((w2 * t.y + w1) * t.y + w0)));
    }

    return sqrt(d);
}

// Parametric definition of a rational quadratic bezier
// I prefer to call it "weighted"
vec2 weightedBezier(in vec3 a, in vec3 b, in vec3 c, in float t) {
    a.xy *= a.z, b.xy *= b.z, c.xy *= c.z;
    return mix(mix(a.xy, b.xy, t), mix(b.xy, c.xy, t), t) /
           mix(mix(a.z, b.z, t), mix(b.z, c.z, t), t);
}

float sdLine(in vec2 p, in vec2 a, in vec2 b) {
    vec2 pa = p - a, ba = b - a;
    return length(pa - ba * clamp(dot(pa, ba) / dot(ba, ba), 0.0, 1.0));
}

#define drawSDF(dist, col) color = mix(color, col, smoothstep(unit, 0.0, dist))
void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 center = 0.5 * iResolution.xy;
    vec2 uv = (fragCoord - center) / iResolution.y;
    vec2 mouse = (iMouse.xy - center) / iResolution.y;
    float unit = 2.0 / iResolution.y;

    // Control points with weights
    float r = 0.5 * iTime;
    vec2 anim = 0.1 * vec2(cos(r), sin(r));
    vec3 a = vec3(-0.3 + anim.y, -0.2 + anim.x, 0.5);
    vec3 b = vec3(0.2 * cos(iTime), 0.2 + 0.2 * sin(iTime), mix(-0.3, 3.0, 0.5 + 0.5 * cos(iTime)));//0.7 + cos(2.5 * iTime));
    vec3 c = vec3(0.3 + anim.x, -0.2 + anim.y, 1.5);

    // Set middle control point to the mouse position
    // on mouse down
    if (iMouse.z > 0.0) b.xy = mouse;

    float dist = sdWeightedBezier(uv, a, b, c);

    // Trying a slightly different style
    vec3 color = vec3(0.0);
    color += (0.6 + 0.4 * sin(dist * 300.0)) * vec3(1.0, 0.5, 0.6);
    color *= clamp(pow(3.0 * dist, 0.4), 0.0, 1.0);

    // Hull
    drawSDF(sdLine(uv, a.xy, b.xy) - 0.002, vec3(0.0, 0.0, 1.0));
    drawSDF(sdLine(uv, b.xy, c.xy) - 0.002, vec3(0.0, 0.0, 1.0));

    // Curve
    drawSDF(dist - 0.003, vec3(1.0));

    // Testing
    //vec2 prev = a.xy;
    //float tStep = 0.01;
    //for (float t=0.0; t < 1.0; t += tStep) {
    //    vec2 cur = weightedBezier(a, b, c, t);
    //    drawSDF(sdLine(uv, prev, cur) - 0.003, vec3(0.0, 1.0, 0.0));
    //    prev = cur;
    //}

    // Draw control points
    drawSDF(length(uv - a.xy) - 0.013, vec3(1.0, 1.0, 0.0));
    drawSDF(length(uv - b.xy) - 0.013, vec3(1.0, 0.0, 0.0));
    drawSDF(length(uv - c.xy) - 0.013, vec3(1.0, 1.0, 0.0));

    fragColor = vec4(color, 1.0);
}

