/* Backup (I'm paranoid about making mistakes ;))
// Quartic solver I made here: https://www.shadertoy.com/view/fsB3Wt
int solveQuartic(in float a, in float b, in float c, in float d, in float e, inout vec4 roots) {
    b /= a; c /= a; d /= a; e /= a; // Divide by leading coefficient to make it 1

    float bb = b * b;
    float p = c - 0.375 * bb;
    float q = d - 0.5 * b * c + 0.125 * bb * b;
    float r = e - 0.25 * b * d + bb * c / 16.0 - 3.0 * bb * bb / 256.0;
    int n = 0;

    // Solve for an arbitary root to x^3 + 2px^2 + (p^2 - 4r)x - q^2
    // I really want to use my simpler solver but it has worse precision :(
    float ra = 2.0 * p;
    float rb = p * p - 4.0 * r;
    float rc = -q * q;

    float raa = ra * ra;
    float inflect = ra / 3.0;

    float rp = rb - raa / 3.0;
    float rq = raa * ra / 13.5 - ra * rb / 3.0 + rc;
    float rppp = rp * rp * rp, rqq = rq * rq;

    float p2 = abs(rp);
    float v1 = 1.5 / rp * rq;

    float lambda;
    if (rqq * 0.25 + rppp / 27.0 > 0.0) {
        float v2 = v1 * sqrt(3.0 / p2);
        if (rp < 0.0) lambda = sign(rq) * cosh(acosh(v2 * -sign(rq)) / 3.0);
        else lambda = sinh(asinh(v2) / 3.0);
        lambda = -sqrt(p2 / 3.0) * lambda;
    }

    else lambda = sqrt(-rp / 3.0) * cos(acos(v1 * sqrt(-3.0 / rp)) / 3.0);
    lambda = 2.0 * lambda - inflect;

    // Solve two quadratic equations factored from the quartic using the cubic root
    if (lambda < 0.0) return n;
    float sqrtLambda = sqrt(lambda);

    float pLambda = p + lambda, qLambda = q / sqrtLambda;
    float offs = 0.25 * b;

    float foo = lambda - 2.0 * (pLambda + qLambda);
    float bar = lambda - 2.0 * (pLambda - qLambda);

    if (foo >= 0.0) {
        roots.xy = (vec2(1.0, -1.0) * sqrt(foo) + sqrtLambda) * 0.5 - offs;
        n += 2;
    }

    if (bar >= 0.0) {
        vec2 others = (vec2(1.0, -1.0) * sqrt(bar) - sqrtLambda) * 0.5 - offs;
        if (n > 0) roots.zw = others;
        else roots.xy = others;
        n += 2;
    }

    return n;
}

