	ro.z = sin(th) * sin(ph) * camdist;
	ro.y = cos(th) * camdist;


	vec3 lookAt = sph1.xyz;
	vec3 front = normalize(lookAt - ro);
	vec3 left = normalize(cross(vec3(0,1,0), front));
	vec3 up = normalize(cross(front, left));
