#define SEED_FIXED 39.3
#define SEED_ANIMATED floor(iTime + 39.0)
#define SEED SEED_ANIMATED

#define POINT_COUNT 50
#define VISUALIZE_POINT true
#define VERY_LARGE_NUMBER 999999999.





float fit01(float val, float a, float b)
{
    return a+val*(b-a);
}

vec2 fit01(vec2 val, float a, float b)
{
    return vec2(fit01(val.x,a,b), fit01(val.y,a,b));
}





//bad rng..
float r11(float seed)
{
    return fract(sin(499.0*seed + 199.0)*5869.0);
}

vec2 r12(float seed)
{
    float x = fract(sin(509.0*seed)*197.0);
    float y = fract(sin(487.0*seed + 991.0)*991.0);
    return vec2(x,y);
}

vec3 r13(float seed)
{   
    float x = fract(sin(277.0*seed)*613.0);
    float y = fract(sin(97.0*seed + 607.0)*394.0);
    float z = fract(sin(349.0*seed + 431.0)*124.0);
    return vec3(x,y,z);
}





void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = (2.0*fragCoord.xy-iResolution.xy) / iResolution.y;

    //initialize points
    vec2 points[POINT_COUNT];
    for(int i=0; i<POINT_COUNT; i++)
    {
        float aspect = iResolution.x/iResolution.y;
        points[i] = r12(float(i) * SEED);
        points[i].x = fit01(points[i].x, -aspect,aspect);
        points[i].y = fit01(points[i].y, -1.0,1.0);
    }

    //find closest point
    float closestPointDistance = VERY_LARGE_NUMBER;
    int closestPointIndex = 0;
    for(int i=0; i<POINT_COUNT; i++)
    {
        vec2 pPos = points[i];
        float d = length(pPos - uv);
        if(d < closestPointDistance)
        {
            closestPointDistance = d;
            closestPointIndex = i;
        }
    }

    //random color for each cell
    vec3 col = r13(float(closestPointIndex) * SEED);

    if(VISUALIZE_POINT)
    {
        float pointVisualize = 0.;
        for(int i=0; i<POINT_COUNT; i++)
        {
            vec2 pPos = points[i];
            float d = length(pPos - uv);
            pointVisualize += smoothstep(0.01,0.009, d);
        }
        col *= (1.0-pointVisualize);
    }

    fragColor = vec4(col,1.0);
}

struct face{
    vec3 p;
    vec3 side0;
    vec3 side1;
};

struct ray{
	vec3 p;
	vec3 vec;
};

vec4 create_orientation(vec3 axis, float angle){
	float len;
	vec4 outvec;

	len = length(axis);
	axis *= sin(angle)/len;
	outvec.yzw = axis;
	outvec.x = cos(angle);

	return outvec;
}

vec4 inverse_orientation(vec4 orientation){
	return vec4(orientation.x, -orientation.yzw);
}

vec4 compose_orientation(vec4 a, vec4 b){
	vec4 outvec;

    outvec.x = dot(a, b.xyzw*vec4(1.0, -1.0, -1.0, -1.0));
    outvec.y = dot(a, b.yxwz*vec4(1.0, 1.0, 1.0, -1.0));
    outvec.z = dot(a, b.zwxy*vec4(1.0, -1.0, 1.0, 1.0));
    outvec.w = dot(a, b.wzyx*vec4(1.0, 1.0, -1.0, 1.0));

    return outvec;
}

vec3 apply_orientation(vec3 p, vec4 o){
	vec4 v;

	v.x = 0.0;
	v.yzw = p.xyz;
	return compose_orientation(compose_orientation(o, v), inverse_orientation(o)).yzw;
}

void ray_face_intersect(face f, ray r, out float t, out vec2 face_coords, out vec3 intersect_pos){
    vec3 normal_vec;

    normal_vec = cross(f.side0, f.side1);
    t = dot(f.p - r.p, normal_vec)/dot(r.vec, normal_vec);
    intersect_pos = r.vec*t + r.p;
    face_coords.x = dot(intersect_pos - f.p, f.side0)/(dot(f.side0, f.side0));
    face_coords.y = dot(intersect_pos - f.p, f.side1)/(dot(f.side1, f.side1));
}

float hash(const float n){
    return fract(cos(2.0734*n)*sin(n*1.1234512)*12111.312);
}

