//SDFs
float sdBox(vec3 p, vec3 s) {
    p = abs(p)-s;
	return length(max(p, 0.))+min(max(p.x, max(p.y, p.z)), 0.);
}

float sdPlane(vec3 p,float y){
    return abs(p.y-y);
}

float sdSphere(vec3 p, float r){
    return length(p)-r;
}

//transformations
mat2 Rot(float a) {
    float s = sin(a);
    float c = cos(a);
    return mat2(c, -s, s, c);
}

#define UV   (XY/iResolution.xy)

#define M(tFac)                                                                        \
void mainImage( out vec4 RGBA, in vec2 XY )                                            \
{                                                                                      \
    RGBA = mixTheThings(UV, iTime * tFac, iChannel0, iChannel1, iChannel2, iChannel3); \
}

float us(float t) { return sin(t) * 0.5 + 0.5; }

mat2 rot2(float a) { return mat2(cos(a), sin(a), -sin(a), cos(a)); }

vec4 mixTheThings(in vec2 uv, in float t, in sampler2D s0, in sampler2D s1, in sampler2D s2, in sampler2D s3) {

    uv -= 0.5;
    uv *= rot2(t);
    uv += 0.5;

    vec2 pq = fract(uv * 2.0);

    pq -= 0.5;
    pq *= rot2(t * -1.5);
    pq += 0.5;

    vec4 c;
    if (uv.x < 0.5) {
        if (uv.y < 0.5) {
            c = texture(s0, pq, 0.0);
        }
        else {
            c = texture(s1, pq, 0.0);
        }
    }
    else
    {
        if (uv.y < 0.5) {
            c = texture(s2, pq, 0.0);
        }
        else {
            c = texture(s3, pq, 0.0);
        }
    }

    c *= smoothstep(1.0, 0.95, 2.0 * length(pq - 0.5));

    return c;
}

