float noise(vec3 x) // iq's 3D noise
{
    vec3 f = fract(x);
    vec3 p = x - f;
    f = f*f*(3.0 - 2.0*f);
    vec2 uv = (p.xy + vec2(37.0, 17.0) * p.z) + f.xy;
    vec2 rg = texture(iChannel1, (uv + 0.5)/256.0, -100.0).rg;
    return mix(rg.y, rg.x, f.z);
}

float fbm(vec3 x)
{
    float r = 0.0;
    float w = 1.0, s = 1.0;
    for (int i=0; i<5; i++)
    {
        w *= 0.5;
        s *= 2.0;
        r += w * noise(s * x);
    }
    return r;
}


vec2 mapRope(vec3 p, bool reflection)
{
    vec2 res = vec2(1000.,-1.);

    for(int id = 0; id < NUM_OBJECTS;id++)
    {
        vec4 rope_data = texelFetch(POSITION_TRAIL,ivec2(1,id),0);
        vec3 rope_head = rope_data.xyz;
        float dist_head = clamp(0.,1.,length(rope_head-p));
        vec4  rope = Rope(p,id,POSITION_TRAIL);
        vec3  motion_dir = rope.xyz;
        vec3  position_front =  vec3(0);	
        float envelope = rope_data.w;
        vec3  position_back = normalize(-motion_dir)*ROPE_LENGTH*(.2*tri(envelope*envelope)+0.8);
        float capsule = sdCapsule(p-rope_head, position_back,position_front, CAP_RADIUS);
        res.y = res.x < capsule ? res.y :  float(id);
        res.x = min(res.x, capsule);
        vec4 rope_tail = texelFetch(POSITION_TRAIL,ivec2(ROPE_POINTS,id),0);

        if(reflection)
        {
            //add glowinbg particles
            float b=cos(p.z*.5); 
            vec3 pp = p;
            pp += texture(iChannel0,abs(p.xz*0.01)).xyz*0.5;
            float particle =max(length(cos(pp*1.75+vec3(b,b*.5,cos(pp.x)))),abs(pp.x+pp.y+pp.z)-10.1); //make particles as 0 radius spheres, using cos of original op position and animated up  wish dance bop offset
            particles+=0.4/(0.1+particle*particle*particle*100.);
        }
        glow += 0.1/(0.1+capsule*capsule*10.);
    }
    return vec2(res.x*RAY_MARCH_CORRECTION,res.y);
}


vec4 fire(vec3 p, float id)
{    
    vec4 data = texelFetch(POSITION_TRAIL,ivec2(1,id),0);
    vec3 pos = data.xyz;
    float env = data.w;
    float density = fbm(5.0*(env*1.5)*(p-pos));
    float rad = pow(length(p-pos),.5);
    density -= 0.1 + rad* (0.5 - 0.5 *0.2) ;
    density = clamp(density, 0.0, 1.0);
    density *= (1.1 - smoothstep(0.2, 1., rad));
    vec3 col = getColor(rad,density);
    density *= smoothstep(0.1,0.4,dot(col,col));
    return vec4(col, density);
}

vec4 fill_fire(vec3 ro, vec3 rd, vec2 obj)
{
    vec4 data = texelFetch(POSITION_TRAIL,ivec2(1,obj.y),0);
    vec3 pos = data.xyz;
    float env = tri(data.w*data.w);
    vec3 p = ro + rd * obj.x; 
    vec4 color = vec4(0);
    vec3 position_rope = vec3(0);
    for (int i=0; i<NUM_SMOKE_STEPS; i++)
    {
        if (color.a > 0.99 || color.a > 0.) continue;
        //if (length(p) > 1.1) continue;
        vec4 d = fire(p, obj.y);
        //float dist_from_head = clamp(length(p-pos),0.,1.);
        d.rgb *= d.a;
        color += d * (10. - color.a);
        p += rd * MIN_DIST;
    }

    float d = length(p-pos);
    color *= 1.1/(0.1+d*d*d*10.);
    return color;
}


vec2 intersectRope(vec3 ro, vec3 rd, bool reflection)
{
    float dist = 0.01;
    float id = -1.;
    for(int i = 0; i < 128; i++)
    {
        vec3 p = ro + rd*dist;
        vec2 hit = mapRope(p,reflection);
        id = hit.y;
        if( dist > 20. || abs(hit.x) < 0.01) break;
        dist += hit.x;
    }
    return vec2(dist, id);
}


vec3 normalRope(vec3 p)
{
    vec2 e = vec2(1.0,-1.0)*0.00025773;
    return normalize( e.xyy*mapRope( p + e.xyy, false).x + 
					  e.yyx*mapRope( p + e.yyx, false).x + 
					  e.yxy*mapRope( p + e.yxy, false).x + 
					  e.xxx*mapRope( p + e.xxx, false).x );
}



vec3 normal_fire(vec3 p, float id)
{
    vec2 e = vec2(1.0,-1.0)*0.0025773;
    return normalize( e.xyy*fire( p + e.xyy, id).w + 
					  e.yyx*fire (p + e.yyx, id).w + 
					  e.yxy*fire( p + e.yxy, id).w + 
					  e.xxx*fire( p + e.xxx, id).w );
}

vec3 normalSphere(vec3 x)
{
    float s = length(x)-CENTER_SPH_RAD,
        dx = 5.e-4;
    return normalize(vec3(
        length(x+dx*c.xyy)-CENTER_SPH_RAD, 
        length(x+dx*c.yxy)-CENTER_SPH_RAD, 
        length(x+dx*c.yyx)-CENTER_SPH_RAD
    )-s);
}

mat3 camera( in vec3 ro, in vec3 ta, float cr )
{
	vec3 f = normalize(ta-ro);
	vec3 cp = vec3(sin(cr), cos(cr),0.0);
	vec3 r = normalize( cross(f,cp) );
	vec3 u =          ( cross(r,f) );
    return mat3( r, u, f );
}

vec3 renderCol(vec2 hit, vec3 ro, vec3 rd, bool reflection)
{
    vec3 lig_pos = vec3(0,1,0);
    vec3 col = vec3(0);
    vec3 p = ro +rd*hit.x;

    vec4 smoke = fill_fire(ro,rd,hit);
    col = max(smoke.xyz,vec3(0));
    if(dot(col,col) < 0.001) return vec3(0);
    float id =  hit.y;
    vec3 norm_fire = normal_fire(p, id);
    float diff = dot(norm_fire, -lig_pos)*0.5+0.5;
    col *= diff;
    col = max(col*1.,vec3(0));
    return col;
}

vec3 render(vec3 ro, vec3 rd)
{
    vec3 col =vec3(0);
    bool reflection = false;
    vec2 sph = asphere(ro, rd, CENTER_SPH_RAD);
    vec2 rope = intersectRope(ro,rd,reflection);
    float dist_front = sph.x;
    float dist_back = sph.y;
    vec2 front, back;
    vec3 ro_front, ro_back, rd_front, rd_back, norm_front, norm_back;
    if(sph.x < 15. && sph.y < 30.)
    {
        vec3 p_front = ro+rd*dist_front;
        norm_front = normalSphere(p_front);
        ro_front = p_front;
        vec3 ref_front = reflect(rd,norm_front);
        rd_front = ref_front;
        reflection = true;
        front = intersectRope(ro_front,rd_front,reflection);
        col +=texture(iChannel2,rd_front).xyz*0.045;
        col += max(vec3(0),renderCol(front, ro_front, rd_front, reflection)*0.5);

        vec3 p_back = ro+rd*dist_back;
        norm_back = normalSphere(p_back);
        ro_back = p_back;
        vec3 ref_back = reflect(rd,norm_back);
        rd_back = ref_back;
        back = intersectRope(ro_back,rd_front,reflection);
        vec3 col_back = max(vec3(0),renderCol(back, ro_back, rd_back, reflection))*0.3;
        col_back +=texture(iChannel2,rd_back).xyz*0.025;
        col_back = mix(vec3(0),col_back,0.4);
        col += col_back;
    }else
    {
        col =texture(iChannel2,rd).xyz*0.14;   
    }
    float dist_min = min(dist_front,dist_back);
    float atten = reflection ? 0.5  : 1.;
    vec3 col_rope = rope.y < fNUM_OBJECTS ? renderCol(rope, ro, rd, reflection)*atten : vec3(0);
    col = mix(col,col_rope,0.1);    
    vec3 cubem = texture(iChannel2,rd).xyz;
    if(reflection) col += cubem*0.4;
    float pglow = pow(glow,.5);
    col += vec3(particles*pglow*.31/(fNUM_OBJECTS*0.3))*vec3(0.5, 0.4, 0.1);
    return max(col,vec3(0));

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.y*2.;
    vec2 m = vec2(0.5);
	if( iMouse.z>0.0 ) m = iMouse.xy/iResolution.xy;
    float an = 0.5*iTime - TAU*(m.x-0.5);
	vec3 ro = vec3(2.0*sin(an),2.5*cos(0.4*an),2.0*cos(an));
    vec3 lookat = vec3(0.0,0.0,0.0);
    mat3 cam = camera(ro, lookat,0.);
    vec3 rd = cam*vec3(uv,1. );
    vec3 col = vec3(0);
    col = render(ro,rd);
    col = pow(clamp(col,0.,1.), vec3(0.45));
    fragColor = vec4(col,1.0);
}

