//flames are distributed on a sphere in a grid
//#define LO_PERF
#ifdef LO_PERF
#define SPHERE_GRID vec2(1,2)
#else
#define SPHERE_GRID vec2(3,3)
#endif
const int NUM_OBJECTS =  int(SPHERE_GRID.x*SPHERE_GRID.y);
const float fNUM_OBJECTS = float(NUM_OBJECTS);
#define PI 3.14159
#define TAU 6.28359
#define MIN_DIST 0.001
#define MAX_DIST 10.
#define NUM_SMOKE_STEPS 10
#define ROPE_POINTS 32
#define ROPE_LENGTH 0.6
const float fROPE_POINTS = float(ROPE_POINTS);
#define POSITION_TRAIL iChannel0
#define CAP_RADIUS 0.08
#define RAY_MARCH_CORRECTION 0.7
#define FLAMES_DIST 1.
#define CENTER_SPH_RAD 0.8


float hash( float n )
{
    return fract(sin(n+2910.)*43758.5453123);
}

//Blackle's erot function
vec3 erot(vec3 p, vec3 ax, float ro) {
    return mix(dot(ax,p)*ax, p, cos(ro))+sin(ro)*cross(ax,p);
}

float envelope(int obj, float time)
{
   float  freq = hash(float(obj)*20.);

   return 1.-fract(freq*time);   
}

float slide(float cur, float tar, float slu, float sld)
{
    //slide up or slide down
    float del = (tar>cur) ?  slu : sld;
 	cur += (tar - cur) * del;
    return    cur;
}

vec3 pos_on_sphere(float phi, float theta, float radius)
{
//assumes both phi and theta are 0<>1
        phi *= 2.;
        phi -= 1.;
        phi *= 3.14159;
        theta *= 6.28359; 
        float coth = cos(theta), 
              sith = sin(theta), 
              coph = cos(phi),
              siph = sin(phi);
        float x = radius*coph*coth,
              y = radius*coph*sith,
              z = radius*siph;

       return vec3(x,y,z);
}


float tri(float x)
{
    return min(fract(x) * 2., 2. - 2. * fract(x));
}

vec3 getColor(float r, float d)
{
    vec3 c = mix(vec3(0.9), vec3(0.5, 0.4, 0.1), d);

	c *= mix(vec3(1.0, 0.4, 0.0), vec3(0.5), r );

	return c * 4.0;
}

//IQ 
float sdCapsule( vec3 p, vec3 a, vec3 b, float r)
{
  vec3 pa = p - a, ba = b - a;
  float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );

  return length( pa - ba*h ) - r*sqrt(h);
}
// Analytical sphere distance.
// Thanks Nr4 for this!
//https://shadertoy.com/view/Wlj3z1
const vec3 c = vec3(1.,0.,-1.);
const float f = 1.e4;
vec2 asphere(vec3 x, vec3 dir, float R)
{
    float a = dot(dir,dir),
        b = 2.*dot(x,dir),
        cc = dot(x,x)-R*R,
        dis = b*b-4.*a*cc;
    if(dis<0.) return vec2(f);
    vec2 dd = (c.xz*sqrt(dis)-b)/2./a;
    return vec2(min(dd.x, dd.y), max(dd.x, dd.y));
}



//spline interpolation function to connect points across tentacle 
//from : http://sol.gfxile.net/interpolation/
vec3 catmullrom(float t, vec3 p0, vec3 p1, vec3 p2, vec3 p3)
{
	return 0.5 * (
				(2. * p1) +
				(-p0 + p2) * t +
				(2. * p0 - 5. * p1 + 4. * p2 - p3) * t * t +
				(-p0 + 3. * p1 - 3. * p2 + p3) * t * t * t
				);
}

//this is probably the most relevant function in this shader: it allows for smooth interpolation
//between points in a sdf (e.g. capsule), it gets its points from a row of pixels stored and 
//continuisly updating in buffer B. For this  to work the object needs to be moving, if slightly, at all times
vec4 Rope(vec3 p, int id, in sampler2D text)
{
    vec3  rope_head   = texelFetch(text, ivec2(1, id ), 0).xyz;//position data is stored starting from pix(1,id)
    vec3  rope_tail   = texelFetch(text, ivec2(ROPE_POINTS-1, id ), 0).xyz;
    float speed       = 1.+(length(rope_head - rope_tail))*.3;//TWEAK

    //since the distance from the beginning of the rope changes based on object speed
    //attentuate this undesired effect
    float center_dist = clamp(length(p-rope_head),0.,1.)/speed;

    //pick previous position based on how distant p is from the current position
    int   point_coo    = int(floor(center_dist*(fROPE_POINTS-3.)));
    float interp_index = fract(center_dist*(fROPE_POINTS-3.));
    vec3  rope_point_0 = texelFetch(text, ivec2(point_coo+0, id ), 0).xyz;
    vec3  rope_point_1 = texelFetch(text, ivec2(point_coo+1, id ), 0).xyz;
    vec3  rope_point_2 = texelFetch(text, ivec2(point_coo+2, id ), 0).xyz;
    vec3  rope_point_3 = texelFetch(text, ivec2(point_coo+3, id ), 0).xyz;
    vec3  point_ahead  = catmullrom(interp_index, rope_point_0, rope_point_1, rope_point_2, rope_point_3);
    vec3  point_behind = catmullrom(interp_index-0.051, rope_point_0, rope_point_1, rope_point_2, rope_point_3);
    vec3  motion_dir    = point_behind -point_ahead  ;	

    return vec4(motion_dir, center_dist);
}

