void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    ivec2 iCoo = ivec2(fragCoord);
    if(any(greaterThan(iCoo,ivec2(ROPE_POINTS,NUM_OBJECTS)))) discard;
    if(iCoo.x == 0)
    {
        float new_ramp     = envelope(iCoo.y, iTime);
        vec4  prev_data    = iFrame < 1 ? vec4(0,0.5, 0.6, 1.) : texelFetch(iChannel0,iCoo,0);
        float prev_env     = prev_data.x, 
              prev_ramp    = prev_data.y;
        bool trigger = new_ramp > prev_ramp;
        float env = prev_env;
        if(trigger) env = 1.; 
        else {env = slide(env, 0., 1.,1./20.);}
        fragColor = vec4(env, new_ramp, 0,0);
     }else if (iCoo.x == 1)
     {
         vec2 id = vec2(mod(fragCoord.y,SPHERE_GRID.x)/SPHERE_GRID.x,
                        floor(fragCoord.y/SPHERE_GRID.x));
         vec2 sect = vec2(1)/SPHERE_GRID;
         float off = iCoo.y == 0 || iCoo.y == NUM_OBJECTS-1 ? 0. : sect.y*0.25;
         float phi = off+sect.y*id.y;
         float theta =id.x;
         vec3 pos = pos_on_sphere(phi,theta,FLAMES_DIST);
         float env = texelFetch(iChannel0,ivec2(0,iCoo.y),0).x;
         pos *= (1.01-tri(env)*0.04);//add motion toward sphere
         vec3 pt = cross(pos,vec3(0,1,0)*0.05)+pos;
         vec3 pt_rot = erot(pt,pos,env*TAU);
         fragColor = vec4(pt_rot,env);
     }else
     {
         fragColor.xyz = texelFetch(iChannel0,iCoo-ivec2(1,0),0).xyz+texelFetch(iChannel0,ivec2(1,iCoo.y),0).xyz*(float(ROPE_POINTS-iCoo.x)/fROPE_POINTS*0.1);
     }

}

