#define MAX_STEPS 400
#define MAX_DIST 200.0
#define SURF_DIST 0.0001

#define OBJ1 1.0
#define OBJ2 2.0
#define LIGHT_ID 3.0
#define RIGHT_WALL 4.0
#define LEFT_WALL 5.0



float sdBox( vec3 p, vec3 r )
{
  vec3 q = abs(p) - r;
  return length(max(q,0.0)) + min(max(q.x, max(q.y,q.z)), 0.0);
}

float sdSphere(vec3 p, float r)
{
    return length(p) - r;
}

vec2 minSDF(vec2 sdf1, vec2 sdf2)
{
    return sdf1.x<sdf2.x ? sdf1 : sdf2;
}


vec2 rot_point(vec2 a, float b)
{
    float c = cos(b);
    float s = sin(b);
    return vec2(a.x * c - a.y * s, a.x * s + a.y * c);
}

float hash(float seed)
{
    return fract(sin(seed)*43758.5453);
}

//http://www.amietia.com/lambertnotangent.html
vec3 getBRDFray(vec3 n, float seed)
{
    float u = hash(78.233 + seed);
    float v = hash(10.873 + seed);

    float theta = 6.283185 * v; //2pi*v
    u = 2.0*u - 1.0;
    vec3 sph_point = vec3(sqrt(1.0 - u*u) * vec2(cos(theta), sin(theta)), u);

    return normalize(n + sph_point);
}

vec2 map(vec3 p) //cornell box map
{
    vec2 cur_sdf = vec2(1e10, 1.0);

    float size = 2.0;
    cur_sdf = minSDF(cur_sdf, vec2(sdBox(p-vec3(0.0, -size, 0.0), vec3(size, 0.01, size)), OBJ1));//plane
    cur_sdf = minSDF(cur_sdf, vec2(sdBox(p-vec3(-size, 0.0, 0.0), vec3(0.01, size, size)), LEFT_WALL));//side walls
    cur_sdf = minSDF(cur_sdf, vec2(sdBox(p-vec3(size, 0.0, 0.0), vec3(0.01, size, size)), RIGHT_WALL));
    cur_sdf = minSDF(cur_sdf, vec2(sdBox(p-vec3(0.0, size, 0.0), vec3(size, 0.01, size)), OBJ1));//cealing
    cur_sdf = minSDF(cur_sdf, vec2(sdBox(p-vec3(0.0, 0.0, size), vec3(size, size, 0.01)), OBJ1));//backwall

    cur_sdf = minSDF(cur_sdf, vec2(sdBox(p-vec3(0.0, 1.99, 0.0), vec3(0.5, 0.01, 0.5)), LIGHT_ID));//light


    cur_sdf = minSDF(cur_sdf, vec2(sdSphere(p-vec3(-1.0, -1.5, 0.3), 0.5), OBJ2 ));//sphere
    cur_sdf = minSDF(cur_sdf, vec2(sdSphere(p-vec3(0.2, -1.7, -1.0), 0.3), OBJ2 ));//sphere

    vec3 q = p-vec3(1.0, -1.1, 0.5);
    q.xz = rot_point(q.xz, 0.4);
    cur_sdf = minSDF(cur_sdf, vec2(sdBox(q, vec3(0.4, 0.9, 0.4)), OBJ2));//box
    return cur_sdf;
}

vec3 getNormal(vec3 p)
{
    vec2 e = vec2(0.0001, 0);//error bias
    vec3 n = normalize(vec3(
            map(p + e.xyy).x - map(p - e.xyy).x,
            map(p + e.yxy).x - map(p - e.yxy).x,
            map(p + e.yyx).x - map(p - e.yyx).x));
    return n;
}

vec2 rayMarch(vec3 ro, vec3 rd)
{
    float t = 0.0001;
    vec2 res;
    for(int i = 0; i < MAX_STEPS; i++)
    {
        vec3 p = ro + t*rd;
        res = map(p);
        if(res.x < SURF_DIST || res.x > MAX_DIST) break;
        t += res.x;
    }

    if (t > MAX_DIST) t = -1.0;

    return vec2(t, res.y);
}
