float calcShadow(vec3 ro, vec3 rd)
{
    float t = 0.0001;
    for(int i = 0; i < MAX_STEPS; i++)
    {
        vec3 p = ro + t*rd;
        float res = map(p).x;
        if(res < SURF_DIST || res > MAX_DIST) break;
        t += res;
    }

    return t>MAX_DIST ? 1.0 : 0.0;
}*/

vec4 getMaterial(float objID){

    vec4 col = vec4(vec3(1.0), 0.0);
    if(objID == OBJ2)       {col = vec4(vec3(0.7), 0.0);}
    else if(objID == OBJ1)  {col = vec4(vec3(1.0), 0.0);}//vec3(0.016, 0.02, 0.03)
    else if(objID == RIGHT_WALL)  {col = vec4(vec3(0.7, 0.05, 0.1), 0.0);}
    else if(objID == LEFT_WALL)  {col = vec4(vec3(0.1, 0.7, 0.03), 0.0);}
    else if(objID == LIGHT_ID)  {col = vec4(vec3(1.0, 0.83, 0.71), 4.0);}
    else                    {col = vec4(vec3(0.2), 0.0);}

    return col;
}


#define GI_BOUNCE 6
vec3 pathTraceColor(vec3 ro, vec3 rd, float rngFloat)
{
    vec3 total_col = vec3(0.0);
    vec3 ray_col = vec3(1.0);
    for(int i = 0; i < GI_BOUNCE; i++)
    {
        //raymarch scene/////////
        vec2 res = rayMarch(ro, rd);
        float t = res.x;
        float objID = res.y;

        //check ray didnt escape scene/////////
        if(t < 0.0)  {break;}

        //point position and normal/////////
        vec3 p = ro + t*rd;
        vec3 n = getNormal(p);

        //lighting for current path////////
        vec4 material = getMaterial(objID);
        float emisiv = material.w;

        ray_col *= material.xyz; //add surface colour to ray

        vec3 indirect_light = ray_col * emisiv;
        total_col += indirect_light * ray_col; //indirect + direct light

        //setup new ray/////////
        ro = p+n*0.05;
        rd = getBRDFray(n, 76.2 + 73.1*float(i) + rngFloat + 17.7*float(iFrame));
    }
    return total_col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord)
{

    //center 0,0
    float rng = hash( dot( fragCoord, vec2(12.9898, 78.233) ) + 1113.1*float(iFrame)); //RNG

    //AA through random offset
    vec2 offset = -0.5 + vec2( hash(rng+13.271), hash(rng+63.216) );
    vec2 uv = ((fragCoord+offset) - 0.5*iResolution.xy)/iResolution.y; 


    vec3 ro = vec3(0.0, 0.0,-5.5);
    vec3 rd = vec3(uv.x, uv.y, 1.0);

    vec3 col = texelFetch( iChannel0, ivec2(fragCoord), 0 ).xyz;
    if( iFrame==0 ) {col = vec3(0.0);};

    col += pathTraceColor(ro, rd, rng);


    fragColor = vec4( col , 1.0);

}

