vec2 circlePtAnimated(float lt) {
    return vec2(sin(lt*3.1416*2.0), cos(lt*3.1416*2.0));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.y;


    float curTimeSeconds = iDate.w;
    vec4 millisSecsMinsHours = vec4(
        mod(curTimeSeconds,                      1.0), 
        mod(floor(curTimeSeconds)  /   60.0,     1.0), 
        mod(floor(curTimeSeconds / 60.0) / 60.0, 1.0), 
        mod((curTimeSeconds / 3600.0) / 12.0,    1.0)
        );
    vec4 lineLen = vec4(0.1, 0.8, 0.75, 0.6) * 0.5;

    float lineThickness = 1.0;

    vec2 ptCenter = iResolution.xy/2.0;
