    vec3 dbgCol = vec3(0.0);
    for (int i = 0; i < 4; i++) {
        if (millisSecsMinsHours[1] > 1.0) {
            dbgCol.r = 1.0;
        }

        vec2 ptEnd = ptCenter + lineLen[i] * circlePtAnimated(millisSecsMinsHours[i]) * iResolution.yy;


        // rgb-subpixel antialiasing
        float colCr = lineThickness - distance_to_line (fragCoord - vec2 (1./3., 0.), ptCenter, ptEnd);
        float colCg = lineThickness - distance_to_line (fragCoord,                    ptCenter, ptEnd);
        float colCb = lineThickness - distance_to_line (fragCoord + vec2 (1./3., 0.), ptCenter, ptEnd);

        color += max(vec3(0.0), vec3(colCr, colCg, colCb));

        lineThickness *= 2.;
    }
    float distCt = length((ptCenter.xy - fragCoord.xy) / iResolution.yy);
    float fadeLen = lineThickness / iResolution.y;
#if 0
    float circle = smoothstep(0.95-fadeLen*0.5, 0.95, distCt * 2.) * smoothstep(0.95+fadeLen*0.5, 0.95, distCt * 2.);
    vec4 col = vec4((color+vec3(circle))*vec3(0.9, 0.8, 0.9)*0.1, 1.0);
#else
    vec4 col = vec4(color, 1.0);
#endif
    fragColor = pow(col,vec4(1./2.2));
}

