void mainImage( out vec4 fragColor, in vec2 fragCoord){
    float zoom = 0.3*iTime*iTime/(iTime+2.0);
    vec2 c = (fragCoord-0.5*iResolution.xy)/length(iResolution.xy)+vec2(0.0000001);
    vec2 dc = vec2(1.0/length(iResolution.xy),0.0);
    vec2 z = c;
    vec2 dz = dc;
    float s = 1.0/exp2(16.0);
    int i2 = 0;
    float total = 0.0;

    vec2 z2 = vec2(0.5*log(dot(z,z))+zoom,atan(z.y,z.x));
    vec2 z3 = vec2(z2.x,z2.y-PI*sign(z2.y));
    vec2 z4 = z2-z3;
    vec2 z5 = vec2(z2.x*z2.x-z2.y*z2.y,2.0*z2.x*z2.y);
    z5 = vec2(z.x*z5.x-z.y*z5.y,dot(z,z5.yx));
    z4 = vec2(dot(z4,z5),z4.y*z5.x-z4.x*z5.y)/dot(z5,z5);
    z = vec2(dot(z2,z3),z3.y*z3.x-z3.x*z2.y)/dot(z2,z2);
    dz = vec2(dz.x*z4.x-dz.y*z4.y,dot(dz,z4.yx));
    for (int i=0;i<999;i++){
        vec2 z2 = vec2(0.5*log(dot(z,z)),atan(z.y,z.x));
        vec2 z3 = vec2(z2.x,z2.y-PI*sign(z2.y));
        vec2 z4 = z2-z3;
        vec2 z5 = vec2(z2.x*z2.x-z2.y*z2.y,2.0*z2.x*z2.y);
        z5 = vec2(z.x*z5.x-z.y*z5.y,dot(z,z5.yx));
        z4 = vec2(dot(z4,z5),z4.y*z5.x-z4.x*z5.y)/dot(z5,z5);
        z = vec2(dot(z2,z3),z3.y*z3.x-z3.x*z2.y)/dot(z2,z2);
        dz = vec2(dz.x*z4.x-dz.y*z4.y,dot(dz,z4.yx));
        while(dot(dz,dz)>s*dot(z,z)){
            total += clamp(2.0*(0.7-length(z))*(0.7-length(z)),0.0,1.0);
            s *= 2.0;
            i2++;
            if (i2>=20){
                fragColor = vec4(vec3(total/float(i2)),1.0);
                return;
            }
		}
    }
}

