void rot(inout vec3 p,vec3 a,float r){
	a=normalize(a);
	p=mix(a*dot(p,a),p,cos(r))+sin(r)*cross(p,a)  ;
}

#define sabs(p) sqrt((p)*(p)+1e-5)
#define smin(a,b) (a+b-sabs(a-b))*.5
#define smax(a,b) (a+b+sabs(a-b))*.5

float dodec(vec3 p,float r){
	float G=sqrt(5.)*.5+.5;
	vec3 n=normalize(vec3(G,1,0));
	float d=0.;
    p=sabs(p);
    d=smax(d,dot(p,n));
    d=smax(d,dot(p,n.yzx));
    d=smax(d,dot(p,n.zxy));
	return d-r;
}

float icosa(vec3 p,float r){
	float G=sqrt(5.)*.5+.5;
	vec3 n=normalize(vec3(G,1./G,0));
	float d=0.;
    p=sabs(p);
    d=smax(d,dot(p,n));
    d=smax(d,dot(p,n.yzx));
    d=smax(d,dot(p,n.zxy));
	d=smax(d,dot(p,normalize(vec3(1))));
    return d-r;
}

#define hash(p)fract(sin(p*12345.5))

float stone(vec3 p)
{
  p.y-=iTime;
    float c=2.;
    vec3 e=floor(p/c);
    e = sin(11.0*(2.5*e+3.0*e.yzx+1.345)); 
    p-=e*.6;
    p=mod(p,c)-c*.5;
    rot(p,hash(e+166.887)-.5,iTime*1.5); 
    if(hash(dot(e,vec3(.234,.24,98))+16776.887)<.5)
    {
        return min(.6,icosa(p,.12));    
    }else{
        return min(.6,dodec(p,.12));    

    }
}

float Scale;
float obj(vec3 p){
	rot(p,vec3(.5*cos(iTime*.2),2.*sin(iTime)*.4,sin(iTime*.5)),iTime*.5);
	float s=3.5;
	p=abs(p);
	vec3  off=p*mix(3.,45.,smoothstep(-.5,.97,cos(iTime*.5)));
	for (int i=0; i<7; i++){
        p=1.-abs(abs(p-2.)-1.); 
    	float r=-3.*clamp(.68*max(1.2/dot(p,p),.7),0.,3.);
        s*=r;
		p*=r;
        p+=off;
    }
    s=abs(s);
    Scale=s;
    float a=18.;
    p-=clamp(p,-a,a);
	return length(cross(p,normalize(vec3(1,3,10))))/s;
}

float map(vec3 p){
    float d=dot(p-vec3(0,-3,0),vec3(0,1,0))-1.5;
    d=min(d,dot(p-vec3(0,0,-5),vec3(0,0,1)));
    d= min(d,stone(p));
    return min(d,obj(p));
}

vec3 calcNormal(vec3 pos){
  vec2 e = vec2(1,-1) * 0.005;
  return normalize(
    e.xyy*map(pos+e.xyy)+e.yyx*map(pos+e.yyx)+ 
    e.yxy*map(pos+e.yxy)+e.xxx*map(pos+e.xxx)
  );
}

float march(vec3 ro, vec3 rd, float near, float far)
{
    float t=near,d;
    for(int i=0;i<200;i++)
    {
        t+=d=map(ro+rd*t);
        if (d<0.001) return t;
        if (t>=far) return far;
    }
    return far;
}

float calcShadow( vec3 light, vec3 ld, float len ) {
	float depth = march( light, ld, 0.0, len );	
	return step( len - depth, 0.01 );
}

vec3 randVec(float s)
{
    vec2 n=hash(vec2(s,s+215.3));
    return vec3(cos(n.y)*cos(n.x),sin(n.y),cos(n.y)*sin(n.x));
}

vec3 randCurve(float t,float n)
{
    vec3 p = vec3(0);
    for (int i=0; i<3; i++){
        p+=randVec(n+=365.)*sin((t*=1.3)+sin(t*.6)*.5);      
    }
    return p;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){

    // call soundin shader 
	if (iResolution.y<2000.) {float c=message((fragCoord/iResolution.y-vec2(0.05,0.1))*10.0);if(c>=0.){fragColor=vec4(c);return;}}

    // ++++++++++

    vec2 uv = (fragCoord* 2.0 - iResolution.xy) / iResolution.y;
    vec3 ro = vec3(0,0,4.);
    vec3 rd = normalize(vec3(uv,-2.0));
    vec3 col = vec3(.7,.1,.0)*hash(dot(uv*3.,vec2(.1234,.566)));
	const float maxd = 30.0;
    float t = march(ro,rd,0.0,maxd);
    if(t<maxd)
    {
        vec3 p=ro+rd*t;
        col=cos(vec3(1,5,9)+log(Scale)*.5)*3.5+0.5;
        col=vec3(1);
        vec3 n = calcNormal(p);      
		vec3 lightPos=vec3(5,28,10)*.5;
    	vec3 li = lightPos - p;
		float len = length( li );
		li /= len;
		float dif = clamp(dot(n, li),.5,1.);
        float sha = calcShadow(lightPos,-li,len);
        col *= max(sha*dif, 0.4);
        float rimd = pow(clamp(1.-dot(reflect(-li,n),-rd),0.,1.),2.5);
		float frn = rimd+2.2*(1.-rimd);
    	col *= frn*.9;
        col *= max(0.5+0.5*n.y, 0.0);
        col *= exp2(-2.*pow(max(0.0, 1.0-map(p+n*0.3)/0.3),2.0));
        col += 2.*vec3(0.9,0.5,0.1)*pow(clamp(dot(reflect(rd, n), li), 0.0, 1.0), 80.0);      
        col = mix(vec3(.2,.3,.6),col,exp(-t*t*.002));
    }

    vec3 pos;
    for(float i=1.;i<250.;i++)
    {
        vec3 p=(vec3(hash(i*23.1234),hash(i*56.1234),hash(i*78.1234))*2.-1.)*vec3(5,1,3);
        p+=randCurve(iTime*.5+hash(i*2256.1234)*1000.,(i+67.234)*345.99)*.5;
        if(dot(rd,p-ro)<t)
        {
            float L=length(cross(rd,p-ro));
            float weight=exp(-L*L*5.)*exp(-t*t*.01);
            col = mix(col, vec3(1), weight*.4);			
        }
    }

    col = pow(col,vec3(2.));
    fragColor.xyz = col;
}

