#define bpm 95.
#define key D
#define scale Major_scale

// == seqence == +++++++++++++

float NOTETIME;
int NUMBER;
float VEROCITY;

float BEAT = 30. / bpm; // 8 Beat
float SUM;
float TIME;

void seq(int number, float noteValue, float velocity){
    float tmp = noteValue * BEAT;
    if(SUM <= TIME){
        NOTETIME = min(tmp, TIME - SUM);
        NUMBER = number;
        VEROCITY = velocity;
        if(number == 255){
            NOTETIME = 100.;
            NUMBER = 0;
        }
    }
    SUM += tmp;
}

#define Q(p,v) seq(p,float(v), 1.);
#define S(p,v,s) seq(p,float(v), s);
#define T(v) seq(0,float(v), 1.);
#define U(v,s) seq(0,float(v), s);
#define R(v) seq(255,float(v), 1.);

#define REP(n) TIME=mod(time, BEAT * float(n));
#define FOR(n) for(int i=0;i<n;i++){
#define END }
#define RESET SUM = 0.;

