// ++ Chord fuctions +++++++++++++++++++++
int packChord(int root, ivec3 t, int inv)
{
    int[3] a;
    for(int i=0;i<3;i++){
       a[i]=root+int[](t[1]-12,t[2]-12,t[0],t[1],t[2])[i+2-inv];
    }
    return a[0]+(a[1]<<7)+(a[2]<<14)+(127<<21);
}

int packChord(int root, ivec4 t, int inv)
{
    int[4] a;
    for(int i=0;i<4;i++){
       a[i]=root+int[](t[1]-12,t[2]-12,t[3]-12,t[0],t[1],t[2],t[3])[i+3-inv];
    }
    return a[0]+(a[1]<<7)+(a[2]<<14)+(a[3]<<21);
}

#define unpack4x7(i) ivec4(i&127,i>>7&127,i>>14&127,i>>21&127)

// +++++++++++++++++++++

// OSC
#define osc_sin(x) sin((x)*6.2832)
#define osc_saw(x) (1.0-fract(x)*2.0)
#define osc_sqr(x) sign(0.5-fract(x))
#define osc_tri(x) asin(sin((x)*6.2832))/1.5708

float note2freq(int n){
  return 440.*exp2((float(n)-69.)/12.);
}

// Note numbers
#define C  60
#define Cs 61
#define D  62
#define Ds 63
#define E  64
#define F  65
#define Fs 66
#define G  67
#define Gs 68
#define A  69
#define As 70
#define B  71
#define UP +12
#define DN -12


//Chord types
#define _M    ivec3(0,4,7)
#define _m    ivec3(0,3,7)
#define _6    ivec4(0,4,7,9)
#define _7    ivec4(0,4,7,10)
#define _M7   ivec4(0,4,7,11)
#define _m7   ivec4(0,3,7,10)
#define _add9 ivec4(0,4,7,14)
#define _sus2 ivec3(0,2,7)
