#define Melodic_minor_scale  int[](0,2,3,5,7,9,11)

#define P(n) key + scale[(n-1)%7] + (n-1)/7*12
#define H(r,t,i) packChord(key+scale[r-1],t,i)

// ++ sound srouce ++++++++++++++++
// FM operator -> vec4(Level,Maltiple,Detune,Envelope),operator
#define FM(a,b,c,d, e,f,g,h, i,j,k,l, m,n,o,p) mat4 DAT=mat4(a,b,c,d, e,f,g,h, i,j,k,l, m,n,o,p);

#define OP(a,i) a.x*osc_sin(a.y*exp2(log2(f)+a.z/12.)*t+i)*exp(-t*5./a.w)

#define O1(i) OP(DAT[0], i)
#define O2(i) OP(DAT[1], i)
#define O3(i) OP(DAT[2], i)
#define O4(i) OP(DAT[3], i)

#define A0(F) O4(O3(O2(O1(F*O1(0.)))))
#define A1(F) O4(O3(O2(0.)+O1(F*O1(0.))))
#define A2(F) O4(O3(O2(0.))+O1(F*O1(0.)))
#define A3(F) O4(O3(0.)+O2(O1(F*O1(0.))))
#define A4(F) O4(O3(0.))+O2(O1(F*O1(0.)))
#define A5(F) O4(O1(F*O1(0.)))+O3(O1(F*O1(0.)))+O2(O1(F*O1(0.)))
#define A6(F) O4(0.)+O3(0.)+O2(O1(F*O1(0.)))
#define A7(F) O4(0.)+O3(0.)+O2(0.)+O1(F*O1(0.))


float gNoize(float t, float a, float b, float c){
    float g=fract(sin((t+exp2(a))*exp2(b))*exp2(c))+6.0;
    return exp(-0.08*g*g) *40.0-1.;
}

float kick(float t){
   return (sin(pow(1.0-min(1.0,t*3.5),15.0)*55.0+1.2)*exp(-t*3.0)
    +0.3*sign(0.5-fract(70.0*t))*exp(-t*25.0))
    *cos(120.0*exp(-t*2.0));
}

float snare(float t){
    float n=gNoize(t,0.,10.,10.)*exp(-t*10.);
    float f=t*160.;
    float m2=.3*osc_tri(1.2*f)*exp(-t*3.);
    float m1=.05*osc_sin(10.*f+m2)*exp(-t*3.);
    float c=osc_sin(f+m1)*exp(-t*15.);
return n+c;
}

float hihat(float t)
{
    return gNoize(t,0.,10.,18.)*exp(-t*15.);
}

float lead(float t, float f)
{
    FM(
      .2, 1., 0., 2.,
      .4, 2., .1, 3.,
      .5, 3., 0., 3.,
      .7, 1., 0., 3.
    );
    return A4(5.0);
}

float piano(float t,float f){
    float g=0.,a,b=0.;
    f*=t;
    for(float j=1.;j<8.;j++){
        b+=a=exp(-j*.5);
        g+=a*osc_sin(f*j)*exp(-t*1.5/(1.-j*.1));
    }
    g*=1.+1.5*exp(-5.*t);
    return g/b;
}

float base(float t,float f)
{
	f *= t;
  	return clamp((
        5.0*osc_sin(f)
        + 2.0*osc_sin(f*2.0)
        + osc_tri(f*3.0)
    	)/4.0,-1.0,1.0)*exp(-5.0*t);
}


// ++ tracks +++++++++++++++++++++++++

// kick
float track1(float time){
    RESET
    REP(8)
    T(2)T(2)T(2)T(2)

    return kick(NOTETIME);  
}

// snare
float track2(float time){
  RESET
  REP(8*4)
  FOR(7)
    R(2)T(2)
  END
  R(1)T(1)T(1)T(1)

  return snare(NOTETIME);  
}

// hihat
float track3(float time){
  RESET
  REP(8)
  T(1)T(1)U(1, 2.)T(1)
  T(1)T(1)U(1, 2.)T(1)

  return hihat(NOTETIME);  
}

float track4(float time, out ivec4 chord){

  RESET
  REP(8*4)
  Q(H(4,_M7,0),8)
  Q(H(5,_7,0),8)
  Q(H(3,_m7,0),8)
  Q(H(6,_m7,0),8)

  chord = unpack4x7(NUMBER);
  float gain=0.;
  for(int i=0;i<4;i++){
    if(chord[i]<127)
        gain += 0.3*piano(NOTETIME,note2freq(chord[i]-12));
  }
  return gain;  
}

float track5(float time, ivec4 chord){
    RESET
    REP(4)
    Q(chord[0]-24, 1)
    Q(chord[0]-12, 1)
    Q(chord[0]-24, 1)
    Q(chord[0]-12, 1)

    return base(NOTETIME,note2freq(NUMBER));
}

float track6(float time){
    RESET
    REP(32)
    Q(1,1)
    Q(5,2)
    Q(6,1)
    Q(8,1)
    Q(2,2)
    Q(3,1)
    Q(4,1)
    Q(1,1)
    Q(5,2)
    Q(6,1)
    Q(8,1)
    Q(2,2)
    Q(3,1)
    Q(5,1) 
    Q(1,1)
    Q(5,2)
    Q(6,1)
    Q(8,1)
    Q(2,2)
    Q(3,1)
    Q(4,1)
    Q(1,1)
    Q(5,2)
    Q(6,1)
    Q(8,1)
    Q(2,2)
    Q(6,1)
    Q(3,1)

    return lead(NOTETIME, note2freq(P(NUMBER)));
}


vec2 mainSound( int samp, float time )
{
    vec2 gain = vec2(0);
    gain += vec2(.2)* track1(time); // kick
    gain += vec2(.2)* track2(time); // snare
    gain += vec2(.2)* track3(time); // hihat
    ivec4 chord;
    gain += vec2(.2)* track4(time, chord); // chord
    gain += vec2(.15)* track5(time, chord); // base
    gain += vec2(.1)* track6(time); // lead

    return gain;
}

