// computes the brightness the pixel at a given fractal coordinate should have
vec3 color(vec2 c, vec2 dc){
    vec3 config = texelFetch(iChannel1,ivec2(1,0),0).xyz;
    int formula = int(config.x);
    float param = config.y;
    int iter = int(config.z);
    if (formula!=-1){
        return vec3(fractal(c,dc,iter,vec2(cos(.7*sin(param)),sin(0.7*sin(param)))));
    }else{
        return vec3(0.0);
    }
