void mainImage(out vec4 fragColor, in vec2 fragCoord){
    ivec2 p = ivec2(fragCoord);
    if (p.y>0||p.x>=3){
        discard;
    }else if (iFrame==0){
        fragColor = vec4(defaultValues[p.x],1.0);
    }else{
        fragColor = texelFetch(iChannel0,p,0);
        if (p==ivec2(0)){
            // flag updates
            float keys = keysDown();
            if (fragColor.y==1.0){
                if (keys/*+iMouse.x+iMouse.y*/>0.0){
                    // animation end because of user input
                    fragColor.xyz = vec3(1.0,0.0,1.0);
                }
            }else{
                //refresh flag update
                fragColor.x = min(1.0,keys+step(mod(float(iFrame),30.0),0.0));
                fragColor.z = 0.0;
            }
        }else if(p==ivec2(1,0)){
            // formula & iteration changes
            vec3 flags = texelFetch(iChannel0,ivec2(0),0).xyz;
            fragColor.x = mod(fragColor.x+float(keyPressed(KEY_E)-keyPressed(KEY_Q)),6.0);
            if (flags.y==1.0){
                // animation
                fragColor.y = 0.15*iTime*iTime/(iTime+2.0);
            }else{
                if (flags.z==1.0){
                    fragColor.y = 0.0;
                }
                // normal controls
                float zoom = texelFetch(iChannel0,ivec2(2,0),0).z;
                float paramChange = iTimeDelta/zoom*float(keyState(KEY_RIGHT)-keyState(KEY_LEFT));
                fragColor.y = mod(fragColor.y+paramChange,2.0*PI);
                fragColor.z += min(100.0,fragColor.z*(pow(2.0,iTimeDelta*float(keyState(KEY_R)-keyState(KEY_F)))-1.0));
            }
        }else if(p==ivec2(2,0)){
            // zoom & position changes
            vec3 flags = texelFetch(iChannel0,ivec2(0),0).xyz;
            if (flags.y==1.0){
                // animation
                float theta = 0.3*iTime*iTime/(iTime+2.0);
                float a = 0.5*theta-0.25*PI;
                a = cos(a-0.5*sin(a));
                float b = 1.0-abs(a);
                b = b*b;
                a = sign(a)*(1.0-b*b*b*b*b);
                a = 0.5+0.5*a;
                fragColor.xy = (0.35+a*1.25)*vec2(-sin(theta),-cos(theta));//+vec2((a-1.0)*0.125,0.0);
                fragColor.z = 0.75*(4.0-3.0*a)*(1.0+3.0*(iTime*iTime)/((iTime*iTime)+5.0));
                //fragColor.z = exp(3.0*cos(a));
            }else{
                if (flags.z==1.0){// reset flag
                    //fragColor.xyz = vec3(-0.5,-0.5,2.0);
                }
                // normal controls
                fragColor.xy -= 2.0*iTimeDelta*vec2(float(keyState(KEY_A)-keyState(KEY_D)),float(keyState(KEY_W)-keyState(KEY_S)))/fragColor.z;
                float zoomChange = iTimeDelta*float(keyState(KEY_UP)-keyState(KEY_DOWN));
                fragColor.z *= pow(2.0,zoomChange);
            }
        }
    }
}

