const float PI = 3.14159265358979323846264;

// taken from http://lolengine.net/blog/2013/07/27/rgb-to-hsv-in-glsl
vec3 hsv2rgb(vec3 c){
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

vec3 complexColor(vec2 z){
    float t = length(z);
    t = 2.0*t/(t+1.0);
    float t2 = 1.0-min(1.0,2.0-t);
    float t3 = 1.0-min(1.0,t);
    return hsv2rgb(vec3(0.5+(0.5/PI)*atan(-z.y,-z.x),1.0-t2*t2,1.0-t3*t3));
}

vec2 cmul(vec2 a, vec2 b){
    return vec2(dot(a,vec2(1.0,-1.0)*b),dot(a,b.yx));
}

vec2 cdiv(vec2 a, vec2 b){
    return vec2(dot(a,b),dot(a.yx,vec2(1.0,-1.0)*b))/dot(b,b);
}

vec2 clog(vec2 a){
    return vec2(0.5*log(dot(a,a)),atan(a.y,a.x));
}

