// See the desmos graphs where I worked most of this out:
// Computing major and minor axes: https://www.desmos.com/calculator/12t322u7xa
// Converting ellipse with center and axes to a general quadric: https://www.desmos.com/calculator/tialwuz5wh
// I was thinking of using this to parametrize the intersection
// between a plane and an ellipsoid

// Drawing utility
#define drawSDF(dist, col) color = mix(color, col, smoothstep(unit, 0.0, dist))

// Main stuff
struct Quadric { float a; float b; float c; float d; float e; float f; };
struct Ellipse { vec2 center; mat2 axes; };

float evalQuadric(in vec2 p, in Quadric q) {
    return q.a * p.x * p.x + q.b * p.y * p.y + q.c * p.x * p.y +
           q.d * p.x + q.e * p.y + q.f;
}

vec2 evalQuadricPrime(in vec2 p, in Quadric q) {
    return vec2(2.0 * q.a * p.x + q.c * p.y + q.d,
                2.0 * q.b * p.y + q.c * p.x + q.e);
}

#if 0 // Trig version
Ellipse parametrizeEllipse(in Quadric q) {
    float cx = 2.0 * q.b * q.d - q.c * q.e, cy = 2.0 * q.a * q.e - q.c * q.d;
    float s = q.c * q.c - 4.0 * q.a * q.b;

    float g = ((q.a * cx * cx + q.b * cy * cy + q.c * cx * cy) / s +
                q.d * cx + q.e * cy) / s + q.f;

    float a = 0.5 * atan(q.b - q.a, q.c);
    vec2 k = vec2(cos(a), sin(a));
    float i = k.x - k.y, j = k.x + k.y;

    float ii = i * i, jj = j * j, ij = q.c * i * j;
    vec2 u = vec2( i, j) * sqrt(-g / (q.a * ii + q.b * jj + ij));
    vec2 v = vec2(-j, i) * sqrt(-g / (q.a * jj + q.b * ii - ij));

    return Ellipse(vec2(cx, cy) / s, mat2(u, v));
}

#else // Trigless version
Ellipse parametrizeEllipse(in Quadric q) {
    float cx = 2.0 * q.b * q.d - q.c * q.e, cy = 2.0 * q.a * q.e - q.c * q.d;
    float s = q.c * q.c - 4.0 * q.a * q.b;

    float g = ((q.a * cx * cx + q.b * cy * cy + q.c * cx * cy) / s +
                q.d * cx + q.e * cy) / s + q.f;

    float ba = q.b - q.a, r = ba / q.c;
    float ca = 0.5 * sign(q.c) / sqrt(1.0 + r * r);
    float ch = sqrt(0.5 + ca), sh = sqrt(0.5 - ca) * sign(ba);
    float i = ch - sh, j = ch + sh;

    float ii = i * i, jj = j * j, ij = q.c * i * j;
    vec2 u = vec2( i, j) * sqrt(-g / (q.a * ii + q.b * jj + ij));
    vec2 v = vec2(-j, i) * sqrt(-g / (q.a * jj + q.b * ii - ij));

    return Ellipse(vec2(cx, cy) / s, mat2(u, v));
}
#endif

Quadric implicitizeEllipse(in Ellipse e) {
    mat2 inv = inverse(e.axes);
    vec2 ce = inv * e.center;
    return Quadric(dot(inv[0], inv[0]),
                   dot(inv[1], inv[1]),
                   2.0 * dot(inv[0], inv[1]),
                   -2.0 * dot(inv[0], ce),
                   -2.0 * dot(inv[1], ce),
                   dot(ce, ce) - 1.0);
}

// SDFs
float sdLine(in vec2 p, in vec2 a, in vec2 b) {
    vec2 pa = p - a, ba = b - a;
    return length(pa - ba * clamp(dot(pa, ba) / dot(ba, ba), 0.0, 1.0));
}

float sdVerticalLine(in vec2 p, in float h) {
    return length(vec2(p.x, p.y - h * clamp(p.y / h, 0.0, 1.0)));
}

float sdIsosceles(in vec2 p, in float b, in float h) {
    p.x = abs(p.x);
    float q = clamp((b - p.x + p.y) / (b + h), 0.0, 1.0);
    vec2 re = vec2(p.x - b + b * q, p.y - h * q);
    vec2 be = vec2(p.x - b * min(p.x / b, 1.0), p.y);
    return sqrt(min(dot(re, re), dot(be, be))) * sign(p.x + (p.y - h) * b / h * sign(p.y));
}

float sdVectorArrow(in vec2 p, in vec2 v) {
    float m = length(v); vec2 n = v / m;
    p = vec2(dot(p, n.yx * vec2(1.0, -1.0)), dot(p, n));
    return min(sdVerticalLine(p, m) - 0.05, sdIsosceles(p - vec2(0.0, m), 0.2, 0.35));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 center = 0.5 * iResolution.xy;
    vec4 mouse = (vec4(iMouse.xy, abs(iMouse.zw)) - center.xyxy) / iResolution.y * 8.0;
    vec2 uv = (fragCoord - center) / iResolution.y * 8.0;
    vec3 color = vec3(0.15);
    float unit = 16.0 / iResolution.y;

    // Ellipse position and axes for testing
    vec2 pos = vec2(3.0 * cos(iTime), sin(2.0 * iTime));
    vec2 ab = vec2(2.0 + cos(iTime), 2.0 + sin(iTime));
    float rot = iTime - 2.0 * sin(0.5 * iTime);

    // Rotate and scale
    vec2 u = vec2(cos(rot), sin(rot));
    vec2 v = vec2(-u.y, u.x) * ab.y;
    u *= ab.x;

    // Add some shear
    u.y += 0.5 + cos(iTime);
    v.x += 0.5 - sin(iTime);

    // Implicitize the ellipse and then extract the center and axes
    // This makes it possible to make an identity check
    Quadric q = implicitizeEllipse(Ellipse(pos, mat2(u, v)));
    Ellipse e = parametrizeEllipse(q);

    // Grid
    float spacing = 0.5, hSpacing = 0.5 * spacing;
    drawSDF(abs(mod(uv.x + hSpacing, spacing) - hSpacing) - 0.001, vec3(0.5));
    drawSDF(abs(mod(uv.y + hSpacing, spacing) - hSpacing) - 0.001, vec3(0.5));

    drawSDF(abs(uv.x) - 0.01, vec3(0.6, 0.0, 0.0));
    drawSDF(abs(uv.y) - 0.01, vec3(0.0, 0.0, 0.6));

    // Quadric
    drawSDF(evalQuadric(uv, q) / length(evalQuadricPrime(uv, q)), vec3(0.0, 0.0, 1.0));

    // Draw an outline using the extracted ellipse parameters
    uv -= e.center;
    float tStep = 0.2;
    float coStep = cos(tStep), siStep = sin(tStep);
    mat2 stepMat = mat2(coStep, siStep, -siStep, coStep);
    vec2 prev = vec2(1.0, 0.0);
    for (float t=0.0; t < 6.28; t += tStep) {
        vec2 cur = stepMat * prev;
        drawSDF(sdLine(uv, e.axes * prev, e.axes * cur) - 0.01, vec3(1.0));
        prev = cur;
    }

    // Original axes
    drawSDF(abs(dot(uv, normalize(vec2(-u.y, u.x)))) - 0.02, vec3(1.0, 0.0, 0.0));
    drawSDF(abs(dot(uv, normalize(vec2(-v.y, v.x)))) - 0.02, vec3(0.0, 1.0, 0.0));

    // Computed axes and center
    drawSDF(sdVectorArrow(uv, e.axes[0]), vec3(1.0, 0.5, 0.0));
    drawSDF(sdVectorArrow(uv, e.axes[1]), vec3(0.8, 1.0, 0.0));
    drawSDF(length(uv) - 0.1, vec3(1.0, 0.8, 0.0));

    fragColor = vec4(color, 1.0);
}

vec3 saturate(vec3 c)
{
    return tanh(pow(c,vec3(0.55)));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec4 acc = texture(iChannel0, fragCoord/iResolution.xy);
    fragColor = vec4(saturate(acc.xyz/acc.w), 1.0);
}

