bool getRay(vec2 uv, out vec3 ro, out vec3 rd)
{
    mat3 cam = getCam(get(CamA));

    vec2 apert_cent = -0.*uv;

    vec2 ap = aperture();

    if(!(distance(ap, apert_cent) < 1.0)) return false;

    float apd = length(ap);

    vec3 daperture = ap.x*cam[0] + ap.y*cam[1];

    ro = get(CamP).xyz + aperture_size*daperture;

    float focus =2.5 + 0.8*pow(apd,5.0);


    rd = normalize(focus*(cam*vec3(FOV*uv, 1.0)) - aperture_size*daperture);

    return true;
}

const vec3 BH_pos = vec3(0.0); //BH position
const float BH_R = 0.5; //event horizon radius

//the black hole "force"
vec3 force(vec3 p)
{
    vec3 r = (p - BH_pos);
    float R = length(r);
    return -1.5*BH_R*r/pow(R,5.0);
}

//integration timestep
const float dt = 0.15;

vec4 render(vec2 fragCoord)
{
    fragCoord += rand2();
    vec2 uv = (fragCoord - 0.5*iResolution.xy)/iResolution.y;

    vec3 cp, rd;
    if(!getRay(uv, cp, rd)) return vec4(0,0,0,1);

    vec3 fcol = vec3(0.0);

    //this blog post is used as reference
    //https://rantonels.github.io/starless/ 

    vec3 ro = cp; //initial position
    vec3 rv = rd; //the initial velocity of the virtual ray test particle

    for(int i = 0; i<128; i++)
    {
        //integrate velocity (simple integration)
        rv += force(ro)*dt;
        ro += rv*dt;

        if(distance(BH_pos, ro) < 0.9*BH_R) return vec4(fcol, 1.0); //got below event horizon
    }

    fcol = 15.*pow(texture(iChannel1, rv).xyz,vec3(4.0));

    return vec4(fcol, 1.0);
}

