vec2 hexagon( vec2 p )
{
    // get the hexagonal coord from the cartesian coord system
    // from https://www.shadertoy.com/view/Xd2GR3
	vec2 q = vec2( p.x*2.0*0.5773503, p.y + p.x*0.5773503 );

	vec2 pi = floor(q);
	vec2 pf = fract(q);

	float v = mod(pi.x + pi.y, 3.0);

	float ca = step(1.0, v);
	float cb = step(2.0, v);
	vec2  ma = step(pf.xy, pf.yx);

	return vec2( pi + ca - cb * ma);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //hexagonal coordinate of the fragCoord
    vec2 co = hexagon(fragCoord);

    //normalize the coordinate on the x axis
    co /= iResolution.xy;

    //get the value of the cell
    // cell.x <=> steam amount in the cell
    // cell.y <=> 1 if cell is frozen else 0
    vec2 cell = texture(iChannel0, co*0.5).xy;

    // color stuff
    vec3 col = vec3(0.0, 0.5, 0.7);
    col += cell.x - cell.y;
    fragColor = vec4(col,1.0);
}

