// directions of neighbors on the exagonal grid
const vec2 dir[6] = vec2[](vec2(1.0, 0.0),
                           vec2(-1.0, 0.0),
                           vec2(0.0, 1.0),
                           vec2(0.0, -1.0),
                           vec2(1.0, 1.0),
