int type(vec2 cell){
    //test if the cell is frozen
    if(texture(iChannel0, cell/iResolution.xy).y > 0.5) return 1;

    for(int i = 0; i < 6; i++){ // for all neighbors
        vec2 data = texture(iChannel0, (cell + dir[i])/iResolution.xy).xy;
        if(data.y > 0.5)return 1;//a neighbors is frozen
    }
