#define sat(a) clamp(a,0.,1.)
mat2 r2d(float a){float ca = cos(a),sa=sin(a);return mat2(ca,-sa,sa,ca);}
#define PI 3.14159265

float _bbox(vec3 p, vec3 s)
{
  vec3 l = abs(p)-s;
  return max(l.x,max(l.y,l.z));
}

vec2 map(vec3 p)
{
  float t = iTime*.01;
  float shp = -p.y;//-texture2D(noise, p.xz*.01+vec2(sin(t),cos(t))).x*.05;
  p+=vec3(0.,-.75,0.);
  p.yz*= r2d(PI*.2);
  p.xy*= r2d(PI*.25);

  shp = min(shp,_bbox(p,vec3(1.)));
  return vec2(shp,0.);
}

vec3 trace(vec3 ro, vec3 rd, int steps)
{
  vec3 p = ro;
  for (int i = 0; i<steps;++i)
  {
    vec2 res = map(p);
    if (res.x<0.01)
      return vec3(res.x,distance(p,ro),res.y);
    p+= rd*res.x;
  }
  return vec3(-1.);
}

vec3 getCam(vec3 rd, vec2 uv)
{
  float fov = 1.;
  vec3 r = normalize(cross(rd, vec3(0.,1.,0.)));
  vec3 u = normalize(cross(rd,r));
  return normalize(rd+fov*(r*uv.x+u*uv.y));
}

vec3 getNorm(vec3 p, float d)
{
  vec2 e = vec2(0.01,0.);
  return normalize(vec3(d)-vec3(map(p-e.xyy).x,map(p-e.yxy).x,map(p-e.yyx).x));
}

vec3 rdr(vec2 uv)
{
  vec3 col;

  vec3 ro = vec3(0.,-5.,-1.);
  vec3 ta = vec3(0.,0.,0.);
  vec3 rd = normalize(ta-ro);

  rd = getCam(rd,uv);

  vec3 res = trace(ro,rd,32);
  if (res.y>0.)
  {
    vec3 p = ro+rd*res.y;
    vec3 n = getNorm(p,res.x);
    col = n*.5+.5;
    vec3 lpos = vec3(sin(iTime),-1./3.,cos(iTime))*10.;
    vec3 ldir = lpos-p;
    vec3 nldir = normalize(ldir);
    vec3 h = normalize(rd-ldir);
    col = vec3(1.)*pow(sat(-dot(n,h)),.75);
    vec3 ressh = trace(p+n*0.02,nldir,64);
    if (ressh.y >0.)
    {

       col*= .75;
    }
    col += vec3(.4,.5,.7)*.5;
    float dao = 0.6;
    col *= sat(pow(sat(map(p+n*dao).x/dao),.5)+.1);
    col+= pow(1.-sat(-dot(rd,n)),.25)*vec3(1.,.5,.7).yzx*.25;
  }
  return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
  vec2 uv = (fragCoord.xy-vec2(.5)*iResolution.xy) / iResolution.xx;
  uv *= 2.;
  vec3 col = rdr(uv);
  vec3 o = col;
  vec3 inv = 1.-sat(col.zyx);
  inv = mix(inv,1.-inv,sat((uv.x-uv.y+sin((uv.y+uv.x)*5.+iTime*2.)*.1)*400.));
  col = mix(col,inv,1.-sat((sin((uv.y+uv.x)*20.)+.4)*400.));
  col = pow(col,vec3(.5));
  col = mix(col,o,sat((length(uv)-.4)*400.));
  fragColor = vec4(col, 1.0);
}

#define ANIMATED_SEED       floor(iTime * 2.0)
#define FIXED_SEED          32.4
#define BRICK_SIZE          0.1
#define BRICK_ASPECT        3.0
#define BRICK_PADDING       0.1
#define BRICK_PADDING_BLUR  0.1
#define BRICK_COLOR_A       vec3(0.88,0.32,0.28)
#define BRICK_COLOR_B       vec3(0.70,0.15,0.1)
#define BRICK_COLOR_C       vec3(0.1,0.1,0.25)

float r11(float seed)
{
    return fract(sin(42.11*seed + 90.33)*1277.3);
}

float r21(vec2 seed)
{
    return fract(sin(82.33*seed.x + 72.3 * seed.y)*5432.677);
}

void makeBrick(vec2 uv, out vec2 id,out vec2 lv)
{
    vec2 brickUV = vec2(uv.x/BRICK_ASPECT,uv.y);
    id = floor(brickUV / BRICK_SIZE);
    if(int(id.y)%2 == 0)
    {
        brickUV.x += BRICK_SIZE*0.5;
        id = floor(brickUV / BRICK_SIZE);
    }
    lv = fract(brickUV / BRICK_SIZE);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = (2.0*fragCoord.xy - iResolution.xy) / iResolution.y;

    float seed = ANIMATED_SEED;
    //float seed = FIXED_SEED;

    vec2 id;
    vec2 lv;
    makeBrick(uv, id, lv);

    //color brick randomly
    float x1 = smoothstep(0.0,BRICK_PADDING/BRICK_ASPECT,lv.x);
    float x2 = smoothstep(1.0,1.0-BRICK_PADDING/BRICK_ASPECT,lv.x);
    float y1 = smoothstep(0.0,BRICK_PADDING,lv.y);
    float y2 = smoothstep(1.0,1.0-BRICK_PADDING,lv.y);
    float brickInsideMask = x1 * x2 * y1 * y2;

    float colMixParam = r21(id * seed);
    vec3 col = mix(BRICK_COLOR_A, BRICK_COLOR_B, colMixParam);

    col = mix(BRICK_COLOR_C,col ,brickInsideMask);

    fragColor = vec4(col,1.0);
}

