    //p = max(p, (p - ab).yx);
    //float ba = ab.y / ab.x, a = ba * ba + 1.0;
    //float b = p.y - p.x, bb = b * b;
    //return p.x - (sqrt(bb - (bb - ab.y * ab.y) * a) - b) / a;

    // Simpler ellipse distance by iq
    p = abs(p);
    p = max(p, (p - ab).yx);
    float a = dot(ab, ab);
    float b = p.y - p.x;
    return p.x - (ab.y * sqrt(a - b * b) - ab.x * b) * ab.x / a;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    #ifdef DECONSTRUCT
    float shift = 0.5 - 0.5 * cos(0.1 * iTime);
    float scale = mix(1.0, 2.0, shift);

    vec2 uv = abs((fragCoord - 0.5 * shift * iResolution.xy) / iResolution.y) * scale;
    float unit = 2.0 * scale / iResolution.y;

    vec2 ab = vec2(1.25, 0.4);
    float dist = sdEllipse(uv, ab) / scale;

    // Nice colors from iq
    vec3 color = 1.0 - sign(dist) * vec3(0.1, 0.4, 0.7);
    color *= 1.0 - exp(-4.0 * abs(dist));
    color *= 0.8 + 0.2 * cos(240.0 * dist);
    drawSDF(abs(dist) * scale, vec3(1.0));

    // Sample point
    vec2 p = vec2(1.5, 0.5);
    float r = (0.5 + 0.5 * sin(0.25 * iTime)) + 0.25;
    float c = cos(r), s = sin(r);
    p *= mat2(c * ab.x, -s * ab.x, s * ab.y, c * ab.y);

    // Sample Distance
    float pDist = abs(sdEllipse(p, ab));
    drawSDF(length(uv - p) - 0.008, vec3(0.0, 1.0, 0.0));
    drawSDF(abs(max(abs(uv.x - p.x), abs(uv.y - p.y)) - pDist), vec3(0.0, 1.0, 0.0));

    // Vertical edge touching
    if (abs(p.x) - ab.x > abs(p.y)) {
        float seg = length(uv - vec2(p.x - pDist, clamp(uv.y, p.y - pDist, p.y + pDist)));
        drawSDF(seg - 0.008, vec3(1.0, 0.0, 0.0));
        if (abs(uv.x) - ab.x > abs(uv.y)) color.b += 0.5;
        drawSDF(abs(uv.y - uv.x + ab.x) * sqrt(0.5) - 0.003, vec3(0.0));
    }

    // Horizontal edge touching
    else if (abs(p.y) - ab.y > abs(p.x)) {
        float seg = length(uv - vec2(clamp(uv.x, p.x - pDist, p.x + pDist), p.y - pDist));
        drawSDF(seg - 0.008, vec3(1.0, 0.0, 0.0));
        if (abs(uv.y) - ab.y > abs(uv.x)) color.b += 0.5;
        drawSDF(abs(uv.x - uv.y + ab.y) * sqrt(0.5) - 0.003, vec3(0.0));
    }

    // Corner touching
    else {
        drawSDF(abs(uv.y - uv.x + p.x - p.y) * sqrt(0.5) - 0.001, vec3(0.0));
        drawSDF(length(uv - p + pDist) - 0.015, vec3(1.0, 0.0, 0.0));
    }

    #else
    // Alternate reality! :D
    vec2 center = 0.5 * iResolution.xy;
    vec2 uv = (fragCoord - center) / iResolution.y;
    vec2 mouse = (iMouse.xy - center) / iResolution.y;
    float unit = 2.0 / iResolution.y;
    float time = iTime * 0.5;

    // Default before user interaction
    if (ivec2(iMouse.xy) == ivec2(0)) mouse = vec2(0.5, 0.5 * sin(time)) * cos(time);

    vec2 dims = vec2(0.3 + 0.05 * sin(iTime), 0.2 + 0.1 * cos(0.5 * iTime));
    float dist = sdEllipse(uv, dims);

    // Nice colors from iq
    vec3 color = 1.0 - sign(dist) * vec3(0.1, 0.4, 0.7);
    color *= 1.0 - exp(-4.0 * abs(dist));
    color *= 0.8 + 0.2 * cos(240.0 * dist);
    drawSDF(abs(dist), vec3(1.0));

    float mDist = abs(sdEllipse(mouse, dims));
    drawSDF(length(uv - mouse) - 0.008, vec3(0.0, 1.0, 0.0));
    drawSDF(abs(max(abs(uv.x - mouse.x), abs(uv.y - mouse.y)) - mDist), vec3(0.0, 1.0, 0.0));

    #endif

    fragColor = vec4(color, 1.0);
}

