float sdBox( in vec2 p, in vec2 b ) {
    // equation from https://iquilezles.org/articles/distfunctions2d
    vec2 d = abs(p)-b;
    return length(max(d,0.0)) + min(max(d.x,d.y),0.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    // Configs. Feel free to tweak
    vec2 boxDimensions = vec2(500, 300);
    vec2 center = vec2(700, 400);
    float shadowSpread = 20.;

    // Draw box
    float sdBox_ = sdBox(fragCoord.xy - center, boxDimensions / 2.);
    // Draw shadow. Mouse click and drag to move it
    float sdShadow = sdBox(fragCoord.xy - center + (iMouse.xy - center) / 30., boxDimensions / 2.);

    if (sdBox_ <= 0.) {
        // negative sdf -> inside the box
        fragColor = blueish;
    } else if (sdShadow > 0.) {
        // positive sdf -> outside the box. Sdf gets rounder and rounder the further it is from the box
        fragColor = mix(black, white, sdShadow / 20.);
    }
}

