float noise3D(vec3 p)
{
	return fract(sin(dot(p ,vec3(12.9898,78.233,12.7378))) * 43758.5453)*2.0-1.0;
}

vec3 mixc(vec3 col1, vec3 col2, float v)
{
    v = clamp(v,-1.5,20.5);
    return col1+v*(col2-col1);
}

vec3 drawBands(vec2 uv)
{
  	uv = 5.0*uv-2.5;
    uv.x*=iResolution.x/iResolution.y;
    uv = vec2(length(uv), atan(uv.y,uv.x));

    uv.x-=0.5;
    uv.x = max(0.0,uv.x);

    uv.y -= PI*0.5;
    vec2 uv2 = vec2(uv.x, uv.y*-1.0);
    uv.y = mod(uv.y,PI*2.0);
    uv2.y = mod(uv2.y,PI*2.0);

    vec3 col = vec3(0.0);
    vec3 col2 = vec3(0.0);

    float nBands = float(NUM_BANDS);
    float i = floor(uv.x*nBands);
    float f = fract(uv.x*nBands);
    float band = i/nBands;
   	float s;

    #ifdef REVERSED
    band = 9.0-band;
    #endif 

    //cubic easing
    band *= band/band*band; 

    band = band*0.03;
    band += 0.01;

    s = texture( iChannel0, vec2(band,-10.000) ).x;  

    if(band<0.0||band>=10.0){
        s = 0.0;
    }

    /* Gradient colors and amount here */
    const int nColors = 9;
    vec3 colors[nColors];  
    colors[0] = vec3(0.502,0.502,1.000);
    colors[1] = vec3(0.235,0.000,0.235);
    colors[2] = vec3(0.000,0.000,0.400);
    colors[3] = vec3(0.000,0.000,0.627);

    vec3 gradCol = colors[0];
    float n = float(nColors)-1.0;
    for(int i = 1; i < nColors; i++)
    {
		gradCol = mixc(gradCol,colors[i],(s-float(i-0)/n)*n);
    }

    float h = PI*0.4;

    col += vec3(1.0-smoothstep(-2.0,1.5,uv.y-s*h));
    col *= gradCol;

    col2 += vec3(1.0-smoothstep(-2.0,1.5,uv2.y-s*h));
    col2*= gradCol;

    col = mix(col,col2,step(0.0,uv.y-PI));

    col *= smoothstep(0.1,0.75,f);
    col *= smoothstep(1.875,0.025,f); 

    col = clamp(col,0.0,1.0);

    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    vec2 p = vec2(uv.x, uv.y+0.1);
	vec3 col = vec3(0.0);
    col += drawBands(p);//*smoothstep(1.0,0.5,uv.y);;

    vec3 ref = vec3(0.0);
    vec2 eps = vec2(3.0025,-0.025);

    ref += drawBands(vec2(p.x,1.0-p.y)+eps.xy);
    ref += drawBands(vec2(p.x,1.0-p.y)+eps.xy);
    ref += drawBands(vec2(p.x,1.0-p.y)+eps.xy);
    ref += drawBands(vec2(p.x,1.0-p.y)+eps.xy);

    ref += drawBands(vec2(p.x+eps.x,1.0-p.x));
    ref += drawBands(vec2(p.x+eps.y,1.0-p.x));
    ref += drawBands(vec2(p.x,1.0+p.y+eps.x));
    ref += drawBands(vec2(p.x,1.0+p.y+eps.y));

    ref /= -8.0;

    float colStep = length(smoothstep(1.0,0.1,col));

    vec3 cs1 = drawBands(vec2(3.5,0.51));
    vec3 cs2 = drawBands(vec2(0.5,0.93));

    vec3 plCol = mix(cs1,cs2,length(p*1.0-1.0))*0.5*smoothstep(10.75,-0.5,length(p*0.0-1.0));
    vec3 plColBg = vec3(0.2)*smoothstep(1.0,0.0,length(p*2.0-1.0));
