    col += clamp(pl*(0.10-colStep),0.0,1.0);

    col += ref*smoothstep(0.125,1.6125,p.y); 

    col = clamp(col, 0.0, 1.0);

    float dither = noise3D(vec3(uv,time))*9.0/2226.0;
    col += dither;



	fragColor = vec4(col,1.0);
}

