float getVal(vec2 uv)
{
    return length(texture(iChannel0,uv).xyz);
}

vec2 getGrad(vec2 uv,float delta)
{
    vec2 d=vec2(delta,0);
    return vec2(
        getVal(uv+d.xy)-getVal(uv-d.xy),
        getVal(uv+d.yx)-getVal(uv-d.yx)
    )/delta;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec3 n = vec3(getGrad(uv,01.0/iResolution.y),500.+(10.0*cos(iTime/3.)));
    //n *= n;
    n=normalize(n);
    fragColor=vec4(n,1);
    vec3 light = normalize(vec3(1,1,2));
    float diff=clamp(dot(n,light),0.5,1.0);
    float spec=clamp(dot(reflect(light,n),vec3(0,0,-1)),0.0,1.0);
    spec=pow(spec,88.0)*1.25;
    //spec=0.0;
	// Convert the uv's to polar coordinates to scale up  
    vec2 polarUv = (uv * 2.0 - 1.0);
    float angle = atan(polarUv.y, polarUv.x);

    // Scale up the length of the vector by a noise function feeded by the angle and length of the vector
    float ll = length(polarUv)*0.4915+(0.01*sin(iTime));

    vec3 base = texture(iChannel0, uv).rgb;

    // Convert the scaled coordinates back to cartesian
    vec2 offs = vec2(cos(angle)*ll + 0.5, sin(angle)*ll + 0.5);
    vec3 overlay = texture(iChannel1,offs).rgb;

     vec4 col = vec4(base + overlay*(0.478975+(0.1*cos(iTime/4.3))), 1.0);

//    fragColor = col;

    fragColor = texture(iChannel0,uv)*vec4(diff)+vec4(spec)+vec4(col);
}

