// Distance to a capsule in the L-infinity metric space. Compare
// to euclidean: https://www.shadertoy.com/view/3tdSDj
//
// List of some other 2D distances in L-infinity:
//
// https://www.shadertoy.com/playlist/XXccDH

// -------------------------------
// THIS CODE IS VERY INNEFICIENT, RIGHT NOW IT'S DOING A BRUTE
// FORCE COMPUTATION. SOME OTHER DAY I'll GIVE IT A TRY
// -------------------------------


float sdCapsule( in vec2 p, vec2 va, vec2 vb, float rb )
{
    // recenter primitive
    p -= (vb+va)*0.5;
    const float k = sqrt(0.5);
    float l = length(vb-va);
    vec2 u = (vb-va)/l;
    vec2 v = vec2(-u.y,u.x);
    l *= 0.5;
    vec2 a = u*l;
    vec2 w = v*rb;

    // distance to body sides
    float ss = (u.x*u.y>0.0)?1.0:-1.0;
    float de = 1.0/(a.y+ss*a.x);
    vec2 w1 = p-a-w;
    vec2 w2 = p-a+w;
    vec2 q1 = abs(w1+a*clamp(-(w1.y+ss*w1.x)*de, 0.0, 2.0));
    vec2 q2 = abs(w2+a*clamp(-(w2.y+ss*w2.x)*de, 0.0, 2.0));
    float d1 = max(q1.x,q1.y);
    float d2 = max(q2.x,q2.y);
    float d = min(d1,d2);

    // inside circular caps
    vec2 pa = p - a;
    vec2 pb = p + a;
    float da = dot(pa,pa);
    float db = dot(pb,pb);
    if( min(da,db)<rb*rb )
    {
        float s = 1.0; if( db<da ) {pa=pb;s=-1.0;}
        float b1 = 0.5*(pa.x+pa.y);
        float b2 = 0.5*(pa.x-pa.y);
        float c = dot(pa,pa) - rb*rb;
        vec2  t1 = vec2(-b1,b1)+sqrt(b1*b1-c*0.5);
        vec2  t2 = vec2(-b2,b2)+sqrt(b2*b2-c*0.5);
        // 4 solution. Up to 3 can be valid
        if( s*dot(pa+vec2( 1, 1)*t1.x,-u)<0.0 ) d=min(d,t1.x);
        if( s*dot(pa+vec2(-1,-1)*t1.y,-u)<0.0 ) d=min(d,t1.y);
        if( s*dot(pa+vec2( 1,-1)*t2.x,-u)<0.0 ) d=min(d,t2.x);
        if( s*dot(pa+vec2(-1, 1)*t2.y,-u)<0.0 ) d=min(d,t2.y);
        d = -d;
    }
    // outside circular caps
    else 
    {
        vec2  qa = abs(mat2(u.x,-u.y,u.y,u.x)*p) - vec2(l,rb);
        float di = max(qa.x, qa.y);
        // outside circular caps AND outside of body
        if( di>0.0 )
        {
            float dc;

            // first cap
            pa = abs(pa);
            if( abs(pa.y-pa.x)<rb )
            {
                float b = 0.5*(pa.x+pa.y);
                float c = dot(pa,pa) - rb*rb;
                dc = b - sqrt(b*b-c*0.5);
            }
            else
            {
                dc = max(pa.x,pa.y)-rb;
            }
            d = min(d,dc);

            // sadly, we still need to test the second cap
            pb = abs(pb);
            if( abs(pb.y-pb.x)<rb )
            {
                float b = 0.5*(pb.x+pb.y);
                float c = dot(pb,pb) - rb*rb;
                dc = b - sqrt(b*b-c*0.5);
            }
            else
            {
                dc = max(pb.x,pb.y)-rb;
            }            
            d = min(d,dc);
        }
        d *= sign(di);
    }

    return d;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 p = (2.0*fragCoord - iResolution.xy) / iResolution.y;
    vec2 m = (2.0*iMouse.xy - iResolution.xy) / iResolution.y;
    p *= 1.9;
    m *= 1.9;

    if( iMouse.x<0.01 ) m=sin(-0.17*iTime*vec2(1.1,1.3)+vec2(0,2));

    vec2  v1 = cos( iTime*0.5 + vec2(0.0,1.00) + 0.0 );
	vec2  v2 = cos( iTime*0.5 + vec2(0.0,3.00) + 1.5 );
    float ra = 0.3*(0.5+0.5*cos(iTime*1.1+1.0));

    float d = sdCapsule(p, v1, v2, ra);

    vec3 col = vec3(1.0,0.9,1.0) + sign(d)*vec3(-0.3,0.4,0.3);
	col *= 1.0 - exp(-3.0*abs(d));
	col *= 0.8 + 0.2*cos(80.0*d);
	col = mix( col, vec3(1.0), 1.0-smoothstep(0.0,0.015,abs(d)) );

    d = sdCapsule(m, v1, v2, ra);
    col = mix(col, vec3(1.0,1.0,0.0), 1.0-smoothstep(0.004, 0.008, abs(max(abs(p.x-m.x), abs(p.y-m.y)) - abs(d))));
    col = mix(col, vec3(1.0,1.0,0.0), 1.0-smoothstep(0.004, 0.008, length(p-m)-0.015));

	fragColor = vec4(col,1.0);
}

// Desmos graph: https://www.desmos.com/calculator/peu1crmp2n

// Constants
#define EPSILON 1e-3
#define INFINITY 1e20

#define PI 3.14159265359
#define TAU 6.28318530718

// Settings
#define ZOOM 1.0
#define AA 2

// Convenience structs
struct Ray { vec3 pos; vec3 dir; };
struct Light { vec3 pos; vec3 color; };
struct TraceInfo { bool hit; float dist; vec3 pos; vec3 nor; vec3 mat; };

// Axis aligned bbox of a transformed box
// The vector returned gives the upper and lower bounds
// relative to the box's center
vec3 transformedBoxAABB(in vec3 dims, in mat3 mat) {
    vec3 w = mat[0] * dims.x;
    vec3 h = mat[1] * dims.y;
    vec3 d = mat[2] * dims.z;
    vec3 a = w + h, b = w - h;
    return max(max(abs(a + d), abs(a - d)),
               max(abs(b + d), abs(b - d)));
}

// Box intersector adapted from https://www.shadertoy.com/view/ld23DV
bool testAABB(in vec3 pos, in vec3 dims, in Ray ray) {
    ray.pos -= pos;
    vec3 n = ray.pos / ray.dir;
    vec3 k = dims / abs(ray.dir);
    vec3 t1 = -k - n, t2 = k - n;
    float tN = max(max(t1.x, t1.y), t1.z);
    float tF = min(min(t2.x, t2.y), t2.z);
    return tN < tF && tF > 0.0;
}

void addBox(in vec3 pos, in vec3 dims, in mat3 transform, in vec3 mat, in Ray ray, inout TraceInfo scene) {
    ray.pos -= pos;

    mat3 inv = inverse(transform);
    ray.pos = inv * ray.pos;
    ray.dir = inv * ray.dir;

    vec3 n = ray.pos / ray.dir;
    vec3 k = dims / abs(ray.dir);
    vec3 t1 = -k - n, t2 = k - n;
    float tN = max(max(t1.x, t1.y), t1.z);
    float tF = min(min(t2.x, t2.y), t2.z);

    if (tN > tF || tF < 0.0) tN = -1.0;
    vec3 nor = -sign(ray.dir) * step(t1.yzx, t1.xyz) * step(t1.zxy, t1.xyz);

    if (tN > 0.0 && (scene.hit ? tN < scene.dist : true)) {
        scene.hit = true;
        scene.dist = tN;
        scene.pos = transform * (ray.pos + ray.dir * tN);
        scene.nor = transform * nor;
        scene.mat = mat;
        scene.pos += pos;
    }
}

// Very inefficient
void addCylinder(in vec3 pos, in int align, in float height, in float radius, in vec3 mat, in Ray ray, inout TraceInfo scene) {
    ray.pos -= pos;
    if (align == 0) {
        ray.pos.xy = ray.pos.yx;
        ray.dir.xy = ray.dir.yx;
    }

    if (align == 2) {
        ray.pos.yz = ray.pos.zy;
        ray.dir.yz = ray.dir.zy;
    }

    // Quadratic coefficients
    float a = dot(ray.dir.xz, ray.dir.xz);
    float b = 2.0 * dot(ray.pos.xz, ray.dir.xz);
    float c = dot(ray.pos.xz, ray.pos.xz) - radius * radius;

    // Solve with a plane for the base and top
    float discr = b * b - 4.0 * a * c;
    if (discr > 0.0) {
        float hh = 0.5 * height;
        float tBase = +(hh - ray.pos.y) / ray.dir.y;
        float tTop  = -(hh + ray.pos.y) / ray.dir.y;
        vec4 roots = vec4(-(vec2(-1, 1) * sqrt(discr) + b) / a * 0.5, tBase, tTop);

        float t = INFINITY;
        vec3 hitPos, nor = vec3(0.0, -1.0, 0.0);
        int nClosest;
        for (int n=0; n < 4; n++) {
            vec3 hitCandid = ray.pos + ray.dir * roots[n];
            if (roots[n] > 0.0 && roots[n] < t  && abs(hitCandid.y) < hh + EPSILON && dot(hitCandid.xz, hitCandid.xz) < radius * radius + EPSILON) {
                t = roots[n];
                hitPos = hitCandid;
                nClosest = n;
            }
        }

        if (nClosest < 2) nor = vec3(hitPos.xz / radius, 0.0).xzy; // Sides
        else nor.y = sign(2.5 - float(nClosest)); // Base and top
        if (t > 0.0 && t < INFINITY && (scene.hit ? t < scene.dist : true)) {
            scene.hit = true;
            scene.dist = t;
            scene.pos = hitPos + pos;
            scene.nor = nor;
            scene.mat = mat;
            if (align == 0) {
                scene.pos.xy = scene.pos.yx;
                scene.nor.xy = scene.nor.yx;
            }

            if (align == 2) {
                scene.pos.yz = scene.pos.zy;
                scene.nor.yz = scene.nor.zy;
            }
        }
    }
}

// Main scene
TraceInfo traceScene(in Ray ray) {
    TraceInfo scene = TraceInfo(false, -1.0, vec3(0.0), vec3(0.0), vec3(0.0));

    vec3 dims = vec3(1.0);
    dims = vec3(0.75, 1.5, 0.75);

    float y = iTime; // Yaw
    float p = 0.5;   // Pitch
    float r = iTime; // Roll

    float cy = cos(y), sy = sin(y);
    float cp = cos(p), sp = sin(p);
    float cr = cos(r), sr = sin(r);

    mat3 mat = mat3(cy * cr + sy * sp * sr, cp * sr,  sy * cr - cy * sp * sr,
                    sy * sp * cr - cy * sr, cp * cr, -sy * sr - cy * sp * cr,
                                  -sy * cp,      sp,                 cy * cp);

    mat[0].x += sin(iTime);
    mat[2].y += cos(iTime);

    vec3 bbox = transformedBoxAABB(dims, mat);
    if (testAABB(vec3(0.0), bbox, ray)) addBox(vec3(0.0), dims, mat, vec3(1.0, 0.5, 0.0), ray, scene);

    float width = 2.0 * bbox.x;
    addCylinder(vec3(0.0, -bbox.y, -bbox.z), 0, width, 0.05, vec3(0.0), ray, scene);
    addCylinder(vec3(0.0,  bbox.y, -bbox.z), 0, width, 0.05, vec3(0.0), ray, scene);
    addCylinder(vec3(0.0,  bbox.y,  bbox.z), 0, width, 0.05, vec3(0.0), ray, scene);
    addCylinder(vec3(0.0, -bbox.y,  bbox.z), 0, width, 0.05, vec3(0.0), ray, scene);

    float height = 2.0 * bbox.y;
    addCylinder(vec3(-bbox.x, 0.0, -bbox.z), 1, height, 0.05, vec3(0.0), ray, scene);
    addCylinder(vec3( bbox.x, 0.0, -bbox.z), 1, height, 0.05, vec3(0.0), ray, scene);
    addCylinder(vec3( bbox.x, 0.0,  bbox.z), 1, height, 0.05, vec3(0.0), ray, scene);
    addCylinder(vec3(-bbox.x, 0.0,  bbox.z), 1, height, 0.05, vec3(0.0), ray, scene);

    float depth = 2.0 * bbox.z;
    addCylinder(vec3(-bbox.x, -bbox.y, 0.0), 2, depth, 0.05, vec3(0.0), ray, scene);
    addCylinder(vec3( bbox.x, -bbox.y, 0.0), 2, depth, 0.05, vec3(0.0), ray, scene);
    addCylinder(vec3( bbox.x,  bbox.y, 0.0), 2, depth, 0.05, vec3(0.0), ray, scene);
    addCylinder(vec3(-bbox.x,  bbox.y, 0.0), 2, depth, 0.05, vec3(0.0), ray, scene);

    return scene;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 center = 0.5 * iResolution.xy;
    vec2 mouse = ivec2(iMouse.xy) == ivec2(0) ? vec2(0.0) : (iMouse.xy - center) / iResolution.y * PI;
    fragColor = vec4(0.0, 0.0, 0.0, 1.0);

    Light light = Light(vec3(-10.0, 10.0, 10.0), vec3(0.8, 0.2, 0.2));

    // Mouse rotation
    vec2 c = cos(mouse), s = sin(mouse);
    mat2 yaw = mat2(c.x, -s.x, s.x, c.x);
    mat2 pitch = mat2(c.y, -s.y, s.y, c.y);

    // Take multiple subsamples for antialiasing
    float aaStep = 1.0 / float(AA);
    for (float xOffs=0.0; xOffs < 1.0; xOffs += aaStep) {
        for (float yOffs=0.0; yOffs < 1.0; yOffs += aaStep) {
            vec2 uv = (fragCoord + vec2(xOffs, yOffs) - center) / iResolution.y;
            Ray camRay = Ray(vec3(0.0, 0.0, 8.0), normalize(vec3(uv, -ZOOM)));

            // Rotate with mouse
            camRay.pos.yz *= pitch;
            camRay.pos.xz *= yaw;
            camRay.dir.yz *= pitch;
            camRay.dir.xz *= yaw;

            // Render the scene
            TraceInfo scene = traceScene(camRay);
            if (scene.hit) {
                vec3 lightDir = normalize(light.pos - scene.pos);
                float shade = max(0.0, dot(scene.nor, lightDir));
                fragColor.rgb += light.color * scene.mat * shade;
            }

            // Sample the background on a miss
            else fragColor += 1.15 - length(uv);
        }
    }

    // Average and tonemap
    fragColor.rgb = pow(fragColor.rgb / float(AA * AA), vec3(0.75));
}

