    ref += drawBands(vec2(p.x,1.0-p.y)+eps.xy);
    ref += drawBands(vec2(p.x,1.0-p.y)+eps.xy);

    ref += drawBands(vec2(p.x+eps.x,1.0-p.x));
    ref += drawBands(vec2(p.x+eps.y,1.0-p.x));
    ref += drawBands(vec2(p.x,1.0+p.y+eps.x));
    ref += drawBands(vec2(p.x,1.0+p.y+eps.y));

