    vec2 uv101 = fragCoord.xy/iResolution.xy - 0.5,
         uv = uv101*map * vec2(iResolution.x / iResolution.y, 1);

    // Rotation //
    float r = t/1.5; // Angle of Rotation
    uv *= mat2( cos(r), -sin(r), sin(r), cos(r) );

    // Figures //
    if (sum(cos(uv*30.)) > 0.9) col = vec3(uv.x, uv.y, 1); // Multicolored Circles

    float k = (cos(t*2.)*0.5+0.5)*3.+0.5;
