  d = PrRoundCylDf (q.xzy, 0.05, 0.02, 0.05);
  q.y -= 0.15;
  q.yz = Rot2Cs (q.yz, csArmA) - 2. * armLen * csArm.yx;
  q.z = abs (q.z);
  q.yz -= 2. * armLen * (csArm * vec2 (1., -1.)).yx;
  q.yz = Rot2Cs (q.yz, csArm * vec2 (1., -1.));
  q.z -= - armLen;
  d = min (d, PrRoundBoxDf (q, vec3 (0.03, 0.04, armLen), 0.02));
  DMINQ (idArm);
  q.z = abs (q.z) - armLen;
  d = PrRoundCylDf (q.yzx, 0.1, 0.02, 0.08);
  DMINQ (idPiv);
  q = p;
  q.yz -= vec2 (1.85, 2.) + Rot2Cs (vec2 (0., 4. * armLen * csArm.x), csArmA * vec2 (1., -1.));
  d = PrRoundCylDf (q.xzy, 0.2, 0.02, 0.05);
  q.z = abs (q.z);
  q.yz -= vec2 (-0.1, 0.1);
  d = min (d, PrCapsDf (q.xzy, 0.02, 0.05));
  DMIN (idGrbl);
  return dMin;
}

float FlyerDf (vec3 p)
{
  vec3 q;
  vec2 fb;
  float dMin, d;
  p = flMat * (p - vec3 (flPos.xz, flHip.y * flScl).xzy);
  p.y -= 0.5;
  dMin = dstFar / flScl;
  p /= flScl;
  p.y += flHip.y;
  q = p;
  q.y -= flHip.y;
  fb = normalize (vec2 (1.2, 1.));
  for (float k = -1. + float (VAR_ZERO); k <= 1.; k += 2.) {
    q = p;
    q.xz = Rot2D (q.xz, k * pRot);
    q.y -= flHip.y + 2.4 + 1.2 * k;
    d = PrRoundCylDf (q.xzy, 0.2 - 0.05 * k, 0.01, 1.2);
    DMINQ (idPTube);
    q.y -= 1.;
    d = min (max (PrRoundCylDf (vec3 (q.x, Rot2D (q.yz, k * pPitch)).xzy, 3., 0.02, 0.01),
       dot (vec2 (q.x, abs (q.z)), fb)),
       max (PrRoundCylDf (vec3 (q.x, Rot2D (q.yz, - k * pPitch)).xzy, 3., 0.02, 0.01),
       dot (vec2 (- q.x, abs (q.z)), fb)));
    DMINQ (idProp);
  }
  q = p;
  q.y -= flHip.y + 0.1;
  d = PrRoundCylDf (q.xzy, flHip.x, 0.05, 0.2);
  q.y -= 0.4;
  d = min (d, PrRoundCylDf (q.xzy, 0.35 * flHip.x, 0.05, 0.15));
  DMINQ (idFlBod);
  q = p;
  q.y -= flHip.y - 0.2;
  d = PrCapsDf (q.xzy, 0.35 * flHip.x, 0.1);
  DMINQ (idCamV);
  q.yz -= vec2 (0.2, flHip.x);
  d = PrCapsDf (q, 0.15 * flHip.x, 0.2);
  DMINQ (idCamH);
  q = p;
  q.xz = Rot2D (q.xz, pi / 6.);
  q.xz = Rot2D (q.xz, 2. * pi * (floor (6. * atan (q.z, - q.x) / (2. * pi)) + 0.5) / 6.);
  q.xy = Rot2D (q.xy - flHip * vec2 (-1., 1.), aU);
  q.x += 0.5 * legLenU;
  d = PrCapsDf (q.yzx, 0.1 * (0.8 + 0.2 * q.x / (0.5 * legLenU)) / 0.6, 0.5 * legLenU);
  DMINQ (idLeg);
  q.x += 0.5 * legLenU;
  q.xy = Rot2D (q.xy, aD - aU);
  q.x += 0.5 * legLenD;
  d = PrCapsDf (q.yzx, 0.1 * (0.9 + 0.1 * q.x / (0.5 * legLenD)), 0.5 * legLenD);
  DMINQ (idLeg);
  q.x += 0.5 * legLenD;
  q.xy = Rot2D (q.xy, 0.5 * pi - aD);
  q.x -= -0.02;
  d = PrRoundCylDf (q.yzx, 0.3, 0.02, 0.05);
  DMINQ (idLeg);
  if (idVu == 0 && ! isSh) dGlow = min (dGlow, dMin);
  return flScl * dMin;
}

float ObjCRay (vec3 ro, vec3 rd)
{
  float dHit, d;
  dHit = 0.;
  for (int j = VAR_ZERO; j < 120; j ++) {
    d = CarDf (ro + dHit * rd);
    if (d < 0.001 || dHit > dstFar) break;
    dHit += d;
  }
  return dHit;
}

float ObjFRay (vec3 ro, vec3 rd)
{
  float dHit, d;
  dHit = 0.;
  for (int j = VAR_ZERO; j < 120; j ++) {
    d = FlyerDf (ro + dHit * rd);
    if (d < 0.001 || dHit > dstFar) break;
    dHit += d;
  }
  return dHit;
}

vec3 ObjCNf (vec3 p)
{
  vec4 v;
  vec2 e;
  e = vec2 (0.001, -0.001);
  for (int j = VAR_ZERO; j < 4; j ++) {
    v[j] = CarDf (p + ((j < 2) ? ((j == 0) ? e.xxx : e.xyy) : ((j == 2) ? e.yxy : e.yyx)));
  }
  v.x = - v.x;
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

vec3 ObjFNf (vec3 p)
{
  vec4 v;
  vec2 e;
  e = vec2 (0.001, -0.001);
  for (int j = VAR_ZERO; j < 4; j ++) {
    v[j] = FlyerDf (p + ((j < 2) ? ((j == 0) ? e.xxx : e.xyy) : ((j == 2) ? e.yxy : e.yyx)));
  }
  v.x = - v.x;
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

float ObjSShadow (vec3 ro, vec3 rd)
{
  vec3 p;
  float sh, d, h;
  sh = 1.;
  d = 0.02;
  for (int j = VAR_ZERO; j < 30; j ++) {
    p = ro + d * rd;
    h = min (CarDf (p), FlyerDf (p));
    sh = min (sh, smoothstep (0., 0.05 * d, h));
    d += h;
    if (sh < 0.05 || d > dstFar) break;
  }
  return 0.5 + 0.5 * sh;
}

vec4 CarCol ()
{
  vec3 col;
  float a, r;
  if (idObj == idCamP || idObj == idTur || idObj == idAnt) a = atan (qHit.x, qHit.z) / (2. * pi);
  if (idObj == idWhl || idObj == idAxl || idObj == idPiv || idObj == idFuel) r = length (qHit.yz);
  else if (idObj == idCamP || idObj == idTur) r = length (qHit.xz);
  if (idObj <= idPiv) {
    if (idObj == idChas) col = vec3 (1., 1., 0.9) * (0.8 + 0.2 * smoothstep (0., 0.02,
       abs (PrRoundBox2Df (vec2 (qHit.y, abs (abs (qHit.z) - 0.8) - 0.4), vec2 (0.05, 0.15), 0.05))));
    else if (idObj == idFuel) col = vec3 (0.8, 0.85, 0.9) * (0.8 + 0.2 * smoothstep (0., 0.02,
       abs (PrRoundBox2Df (qHit.xz, vec2 (0.3, 0.1), 0.02) - 0.1))) *
       (0.8 + 0.2 * smoothstep (0., 0.02, abs (abs (r - 0.23) - 0.03)));
    else if (idObj == idWhl) col = (r > 0.13) ? vec3 (0.95, 0.95, 1.) * (1. - 0.3 * step (0.47, r)) :
       vec3 (1., 0.5, 0.) * (0.3 + 0.7 * smoothstep (0., 0.02, r - 0.05));
    else if (idObj == idLnk) col = vec3 (0.9, 0.9, 1.);
    else if (idObj == idArm) col = vec3 (0.9, 1., 0.9) * (0.8 + 0.2 * smoothstep (0., 0.02,
       abs (mod (qHit.z + 0.1, 0.2) - 0.1)));
    else if (idObj == idPiv) col = vec3 (0.8, 0.9, 0.9) * (0.5 + 0.5 * smoothstep (0., 0.01,
       min (abs (abs (qHit.x) - 0.04), r - 0.02) - 0.01));
  } else {
    if (idObj == idAxl) col = vec3 (0.9, 0.95, 0.9) * (0.5 + 0.5 * smoothstep (0., 0.02,
       abs (r - 0.07) - 0.01)) * (0.5 + 0.5 * smoothstep (0., 0.01, abs (abs (qHit.x) - 0.06) - 0.01));
    else if (idObj == idTur) col = vec3 (0.9, 0.95, 0.9) * (1. - 0.3 * smoothstep (0., 0.02,
       0.05 - abs (abs (qHit.y) - 0.1)) *  smoothstep (0., 0.02, abs (mod (8. * a + 0.5, 1.) -
       0.5) - 0.35)) * (0.8 + 0.2 * smoothstep (0., 0.02, abs (r - 0.15) - 0.01));
    else if (idObj == idGrbl) col = vec3 (0.9, 0.9, 0.8);
    else if (idObj == idCamR) col = mix (vec3 (0.2), vec3 (0.9, 0.3, 0.3), (1. - step (0., qHit.z) *
       smoothstep (0., 0.01, 0.1 - length (qHit.xy))));
    else if (idObj == idCamP) col = mix (vec3 (0.2), vec3 (0.9, 0.3, 0.3), smoothstep (0., 0.02,
       length (vec2 (Rot2D (qHit.xz, 2. * pi * (floor (8. * a + 0.5) / 8.)).x, qHit.y)) - 0.05)) *
       (0.7 + 0.3 * smoothstep (0., 0.02, abs (r - 0.15) - 0.01));
    else if (idObj == idAnt) col = vec3 (0.5, 0.9, 0.5) * (1. - 0.3 * smoothstep (0., 0.02,
       abs (mod (16. * a + 0.5, 1.) - 0.5) - 0.35));
    else if (idObj == idSol) col = (qHit.y > 0.) ? vec3 (0.9, 0.9, 0.3) : vec3 (0.8, 0.8, 0.7);
  }
  return vec4 (col, 0.1);
}

vec4 FlyerCol ()
{
  vec4 col4;
  float r;
  if (idObj == idFlBod || idObj == idProp) r = length (qHit.xz);
  if (idObj == idFlBod) col4 = vec4 (0.9, 0.9, 1., 0.2) * (0.8 + 0.2 * smoothstep (0., 0.02,
     abs (r - 1.1) - 0.1));
  else if (idObj == idLeg) col4 = vec4 (0.9, 0.9, 1., 0.2) * (0.8 + 0.2 * sin (8. * pi * qHit.x));
  else if (idObj == idPTube) col4 = vec4 (0.8, 1., 0.8, 0.2) * (1. - 0.2 *
     sin (4. * atan (qHit.z, qHit.x)));
  else if (idObj == idProp) col4 = vec4 (1., 0.9, 0.8, 0.2) * (0.9 + 0.1 * sin (8. * pi * r));
  else if (idObj == idCamV) col4 = vec4 (0.1, 0.1, 0.1, 0.2);
  else if (idObj == idCamH) col4 = mix (vec4 (0.1, 0.1, 0.1, 0.2), vec4 (0.6 +
     0.4 * sin (4. * pi * tCur), 0., 0., -1.), step (length (qHit.xy), 0.1));
  return col4;
}

float TrailShd (vec2 p)
{
  vec3 u;
  vec2 gB[2], gF[2], dg, q;
  float st, s, gLen;
  st = 1.;
  for (float kz = -1. + float (VAR_ZERO); kz <= 1.; kz ++) {
    gB[0] = carPos.xz;
    gF[0] = gB[0] + Rot2D (vec2 (0., kz * wlBase.z), - carRot);
    for (int j = VAR_ZERO; j < npTrail; j ++) {
      gB[1] = gB[0];
      gF[1] = gF[0];
      u = Loadv4 (npFst + j).xyz;
      if (u.z >= 0.) {
        gB[0] = u.xy;
        gF[0] = gB[0] + Rot2D (vec2 (0., kz * wlBase.z), - u.z);
        s = 1.;
        if (kz == 0.) {
          dg = gF[0] - gF[1];
          gLen = length (dg);
          if (gLen > 0.) {
            q = Rot2Cs (p - 0.5 * (gF[0] + gF[1]), dg.yx / gLen);
            q.x = abs (q.x) - wlBase.x;
            s = min (s, PrRoundBox2Df (q, vec2 (0.08, 0.5 * gLen), 0.05));
          }
        } else {
          for (float k = -1.; k <= 1.; k += 2.) {
            dg = gF[0] - gF[1];
            gLen = length (dg);
            if (gLen > 0.) {
              q = Rot2Cs (p - 0.5 * (gF[0] + gF[1]) + Rot2D (vec2 (k * wlBase.x, 0.), - u.z),
                 dg.yx / gLen);
              s = min (s, PrRoundBox2Df (q, vec2 (0.08, 0.5 * gLen), 0.05));
            }
          }
        }
        st = min (st, 1. - 0.3 * sqrt (1. - float (j) / float (npTrail)) *
           (1. - smoothstep (0., 0.05, s)));
      } else break;
    }
  }
  return st;
}

#define SQ(x) ((x) * (x))

void SetFlyerConf ()
{
  float legHtMin, legHtMax, toePos, d, h, c;
  legLenU = 2.;
  legLenD = 3.;
  flHip.x = 1.5;
  toePos = 4.;
  legHtMin = 0.45 * (legLenU + legLenD);
  legHtMax = sqrt (SQ (legLenU + legLenD) - SQ (toePos - flHip.x));
  flHip.y = flPos.y / flScl + legHtMin;
  h = min (flHip.y, legHtMax);
  d = sqrt (SQ (h) + SQ (toePos - flHip.x));
  c = (SQ (legLenU) - SQ (legLenD) + SQ (d)) / (2. * legLenU * d);
  aU = acos (clamp (c, -1., 1.)) + acos (h / d) - 0.5 * pi;
  c = (SQ (legLenU) + SQ (legLenD) - SQ (d)) / (2. * legLenU * legLenD);
  aD = aU + acos (clamp (c, -1., 1.)) + pi;
  d = flHip.y - (legLenU + legLenD);
  if (d > 0.) {
    d = 0.13 * pi * smoothstep (0., 5., d);
    aU -= d;
    aD -= d;
  }
}

void SetCarConf ()
{
  float armAng;
  armAng = pi * (0.2 + 0.15 * sin (0.2 * pi * tCur));
  csArm = sin (armAng + vec2 (0.5 * pi, 0.));
  csArmA = sin (-0.2 * pi + 0.5 * armAng + vec2 (0.5 * pi, 0.));
}

vec3 GrndCol (vec3 ro, vec3 rd, float dstGrnd, float sh)
{
  vec3 col, vn;
  vec2 vf;
  float f, h, st;
  f = 1. - smoothstep (0.3, 0.5, dstGrnd / dstFar);
  h = f * Fbm2 (0.5 * ro.xz);
  h = smoothstep (0.5, 0.7, h);
  col = vec3 (0.6, 0.45, 0.2) * (1. - 0.3 * (1. - 0.7 * smoothstep (0.5, 0.8, dstGrnd / dstFar)) *
     Fbm2 (4. * ro.xz) * (1. - 0.5 * h));
  col *= 1. + 0.2 * h;
  st = TrailShd (ro.xz);
  vn = vec3 (0., 1., 0.);
  if (f > 0.) vn = VaryNf (2. * ro, vn, (1. - 0.5 * h) * (1. + 2. * step (st, 0.99)) * f);
  col = col * (0.2 + 0.8 * sh * max (dot (vn, sunDir), 0.)) * (0.3 + 0.7 * st) +
     0.15 * h * step (0.95, sh) * pow (max (0., dot (sunDir, reflect (rd, vn))), 32.);
  return col;
}

vec3 StarPat (vec3 rd, float scl)
{
  vec3 tm, qn, u;
  vec2 q;
  float f;
  tm = -1. / max (abs (rd), 0.0001);
  qn = - sign (rd) * step (tm.zxy, tm) * step (tm.yzx, tm);
  u = Maxv3 (tm) * rd;
  q = atan (vec2 (dot (u.zxy, qn), dot (u.yzx, qn)), vec2 (1.)) / pi;
  f = 0.57 * (Fbm2 (11. * dot (0.5 * (qn + 1.), vec3 (1., 2., 4.)) + 131.13 * scl * q) +
      Fbm2 (13. * dot (0.5 * (qn + 1.), vec3 (1., 2., 4.)) + 171.13 * scl * q.yx));
  return 4. * vec3 (1., 1., 0.8) * pow (f, 16.);
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec4 col4;
  vec3 qHitT, col, vn, bgCol;
  float dstObj, dstCar, dstFlyer, dstGrnd, sh, f;
  int idObjT;
  bool isBg;
  SetCarConf ();
  isBg = false;
  bgCol = vec3 (0.2, 0.15, 0.05);
  isSh = false;
  dstCar = ObjCRay (ro, rd);
  idObjT = idObj;
  qHitT = qHit;
  dGlow = dstFar;
  if (idVu == 0) {
    dGlow /= flScl;
    dstFlyer = ObjFRay (ro, rd);
    dGlow *= flScl;
  } else dstFlyer = dstFar;
  dstGrnd = dstFar;
  dstObj = min (dstCar, dstFlyer);
  if (dstObj < dstFar) {
    if (dstCar < dstFlyer) {
      idObj = idObjT;
      qHit = qHitT;
    }
    ro += dstObj * rd;
    if (dstCar < dstFlyer) {
      vn = ObjCNf (ro);
      col4 = CarCol ();
    } else {
      vn = ObjFNf (ro);
      col4 = FlyerCol ();
    }
    dGlow = dstFar;
  } else if (rd.y < 0.) {
    dstGrnd = - ro.y / rd.y;
    ro += dstGrnd * rd;
  } else {
    isBg = true;
    col = bgCol;
  }
  if (! isBg) {
    if (col4.a >= 0.) {
      isSh = true;
      sh = (min (dstObj, dstGrnd) < dstFar) ? ObjSShadow (ro + 0.01 * vn, sunDir) : 1.;
      if (dstObj < dstFar) col = col4.rgb * (0.2 + 0.2 * max (- dot (vn, sunDir), 0.) +
         0.8 * sh * max (dot (vn, sunDir), 0.)) +
         col4.a * step (0.95, sh) * pow (max (0., dot (sunDir, reflect (rd, vn))), 32.);
      else col = mix (GrndCol (ro, rd, dstGrnd, sh), bgCol, smoothstep (0.5, 1.,
         dstGrnd / dstFar));
    } else col = col4.rgb * (0.5 - 0.5 * dot (vn, rd));
  }
  if (min (dstObj, dstGrnd) >= dstFar) {
    col = bgCol + StarPat (rd, 8.);
    col = mix (vec3 (0.2, 0.2, 1.), col, smoothstep (0., 2., 1e5 * (1. -
       dot (rd, normalize (vec3 (1., 0.1, -1.))))));
  }
  if (idVu == 0) col = mix (col, vec3 (0.4, 0.6, 0.4), exp (- 16. * dGlow) *
     (1. - 0.5 * Noiseff (tCur)));
  return clamp (col, 0., 1.);
}

mat3 FlMat (float flTilt)
{
  vec2 ori, ca, sa;
  ori = vec2 (flTilt, flDir);
  ca = cos (ori);
  sa = sin (ori);
  return mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x) *
         mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y , 0., ca.y);
}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  mat3 vuMat;
  vec4 mPtr, stDat;
  vec3 ro, rd, col, wgBxC, g;
  vec2 canvas, uv, ud, um, wd, mSize, ut[2], mMid[2], cnPos;
  float el, az, asp, aspW, zmFac, s;
  int wgSel;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  asp = canvas.x / canvas.y;
  stDat = Loadv4 (0);
  mPtr.xyz = stDat.xyz;
  wgSel = int (stDat.w);
  stDat = Loadv4 (1);
  carPos = stDat.xyz;
  carRot = stDat.w;
  stDat = Loadv4 (2);
  wlBase = stDat.xyz;
  stDat = Loadv4 (3);
  strRot = stDat.y;
  stDat = Loadv4 (4);
  wlRot[0] = stDat.xy;
  wlRot[1] = stDat.zw;
  stDat = Loadv4 (5);
  cnPos = stDat.xy;
  stDat = Loadv4 (6);
  flPos = stDat.xyz;
  stDat = Loadv4 (8);
  flDir = stDat.x;
  flTilt = stDat.y;
  flMat = FlMat (flTilt);
  pRot = stDat.z;
  pPitch = stDat.w;
  aspW = 1.4;
  mSize = 0.33 * vec2 (aspW, 1.);
  mMid[0] = vec2 (- (asp - mSize.x), 1. - mSize.y);
  mMid[1] = vec2 (asp - mSize.x, 1. - mSize.y);
  ut[0] = abs (uv - mMid[0]) - mSize;
  ut[1] = abs (uv - mMid[1]) - mSize;
  idVu = 0;
  for (int k = 0; k < 2; k ++) {
    if (Maxv2 (ut[k]) < 0.) {
      uv = (uv - mMid[k]) / mSize.y;
      idVu = k + 1;
      break;
    }
  }
  flScl = 0.25;
  SetFlyerConf ();
  az = 0.;
  el = -0.1 * pi;
  if (wgSel == 0 && mPtr.z > 0.) {
    az -= 2. * pi * mPtr.x;
    el -= 0.5 * pi * mPtr.y;
  }
  el = clamp (el, -0.45 * pi, 0.);
  vuMat = StdVuMat (el, az);
  if (idVu == 0) {
    ro = carPos + vuMat * vec3 (0., 3., -20.);
    zmFac = 3.5 + 2. * el;
    rd = vuMat * normalize (vec3 (uv, zmFac)); 
  } else if (idVu == 1) {
    ro = flPos + vec3 (0., 2., 0.);
    zmFac = 0.8;
    rd = normalize (vec3 (uv, zmFac));
    rd.yz = Rot2D (rd.yz, 0.05 * pi);
    rd = rd * flMat;
  } else if (idVu == 2) {
    ro = flPos + vec3 (0., 2., 0.);
    zmFac = 0.4;
    rd = normalize (vec3 (uv, zmFac));
    rd.yz = Rot2D (rd.yz, 0.5 * pi);
    rd.xz = Rot2D (rd.xz, flDir);
  }
  dstFar = 200.;
  sunDir = normalize (vec3 (1., 2., -1.));
  col = ShowScene (ro, rd);
  for (int k = 0; k < 2; k ++) {
    if (Maxv2 (ut[k]) < 0. && Minv2 (abs (ut[k])) * canvas.y < 4.) col = vec3 (0.3, 0.6, 0.7);
  }
  if (idVu == 0) {
    wgBxC = vec3 (0.39, -0.3, 0.135);
    ud = 0.5 * uv - wgBxC.xy * vec2 (asp, 1.);
    s = (length (ud) - wgBxC.z) * canvas.y;
    col = mix (vec3 (0.7), col, smoothstep (0., 1., abs (s) - 1.));
    if (s < 0.) {
      col = mix (vec3 (0.7), col, smoothstep (0., 1., Minv2 (abs (ud)) * canvas.y - 1.));
      col = mix (vec3 (0.3, 0.7, 1.), col, smoothstep (0., 1., abs (length (ud - cnPos) - 0.02) *
         canvas.y - 1.));
    }
    um = vec2 (0.75, -0.96) * vec2 (asp, 1.);
    ud = abs (uv - um) - vec2 (0.35, 0.05);
    if (Maxv2 (ud) < 0.) {
      uv -= um + vec2 (0.1, -0.02);
      wd = vec2 (0.1, 0.06) * vec2 (asp, 1.);
      g = floor (vec3 (5. * flPos.xz, 5. * (flPos.y + 1.)));
      s = ShowInt (uv + vec2 (0.22, 0.), wd, 5., g.x) + ShowInt (uv, wd, 5., g.y) +
         ShowInt (uv + vec2 (-0.22, 0.), wd, 5., g.z);
      col = mix (col, vec3 (0.3, 0.7, 1.), s);
    }
  }
  fragColor = vec4 (col, 1.);
}

float PrRoundBoxDf (vec3 p, vec3 b, float r)
{
  return length (max (abs (p) - b, 0.)) - r;
}

float PrRoundBox2Df (vec2 p, vec2 b, float r)
{
  return length (max (abs (p) - b, 0.)) - r;
}

float PrSphDf (vec3 p, float r)
{
  return length (p) - r;
}

float PrCylDf (vec3 p, float r, float h)
{
  return max (length (p.xy) - r, abs (p.z) - h);
}

float PrRoundCylDf (vec3 p, float r, float rt, float h)
{
  return length (max (vec2 (length (p.xy) - r, abs (p.z) - h), 0.)) - rt;
}

float PrCapsDf (vec3 p, float r, float h)
{
  return length (p - vec3 (0., 0., clamp (p.z, - h, h))) - r;
}

float PrTorusBxDf (vec3 p, vec3 b, float ri)
{
  return length (vec2 (length (max (abs (p.xy) - b.xy, 0.)) - b.z, p.z)) - ri;
}

float Maxv2 (vec2 p)
{
  return max (p.x, p.y);
}

float Minv2 (vec2 p)
{
  return min (p.x, p.y);
}

float Maxv3 (vec3 p)
{
  return max (p.x, max (p.y, p.z));
}

mat3 StdVuMat (float el, float az)
{
  vec2 ori, ca, sa;
  ori = vec2 (el, az);
  ca = cos (ori);
  sa = sin (ori);
  return mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y, 0., ca.y) *
         mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x);
}

vec2 Rot2D (vec2 q, float a)
{
  vec2 cs;
  cs = sin (a + vec2 (0.5 * pi, 0.));
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

vec2 Rot2Cs (vec2 q, vec2 cs)
{
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

float DigSeg (vec2 q)
{
  q = 1. - smoothstep (vec2 (0.), vec2 (0.04, 0.07), abs (q) - vec2 (0.13, 0.5));
  return q.x * q.y;
}

#define DSG(q) k = kk;  kk = k / 2;  if (kk * 2 != k) d += DigSeg (q)

float ShowDig (vec2 q, int iv)
{
  vec2 vp, vm, vo;
  float d;
  int k, kk;
  vp = vec2 (0.5, 0.5);
  vm = vec2 (-0.5, 0.5);
  vo = vp - vm;
  if (iv == -1) k = 8;
  else if (iv < 2) k = (iv == 0) ? 119 : 36;
  else if (iv < 4) k = (iv == 2) ? 93 : 109;
  else if (iv < 6) k = (iv == 4) ? 46 : 107;
  else if (iv < 8) k = (iv == 6) ? 122 : 37;
  else             k = (iv == 8) ? 127 : 47;
  q = (q - 0.5) * vec2 (1.8, 2.3);
  d = 0.;
  kk = k;
  DSG (q.yx - vo);  DSG (q.xy - vp);  DSG (q.xy - vm);  DSG (q.yx);
  DSG (q.xy + vm);  DSG (q.xy + vp);  DSG (q.yx + vo);
  return d;
}

float ShowInt (vec2 q, vec2 cBox, float mxChar, float val)
{
  float nDig, idChar, s, sgn, v;
  q = vec2 (- q.x, q.y) / cBox;
  s = 0.;
  if (Minv2 (q) >= 0. && Maxv2 (q) < 1.) {
    q.x *= mxChar;
    sgn = sign (val);
    val = abs (val);
    nDig = (val > 0.) ? floor (max (log2 (val) / log2 (10.), 0.) + 0.001) + 1. : 1.;
    idChar = mxChar - 1. - floor (q.x);
    q.x = fract (q.x);
    v = val / pow (10., mxChar - idChar - 1.);
    if (idChar == mxChar - nDig - 1. && sgn < 0.) s = ShowDig (q, -1);
    if (idChar >= mxChar - nDig) s = ShowDig (q, int (mod (floor (v), 10.)));
  }
  return s;
}

const float cHashM = 43758.54;

vec2 Hashv2f (float p)
{
  return fract (sin (p + vec2 (0., 1.)) * cHashM);
}

vec2 Hashv2v2 (vec2 p)
{
  vec2 cHashVA2 = vec2 (37., 39.);
  return fract (sin (dot (p, cHashVA2) + vec2 (0., cHashVA2.x)) * cHashM);
}

float Noiseff (float p)
{
  vec2 t;
  float ip, fp;
  ip = floor (p);
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = Hashv2f (ip);
  return mix (t.x, t.y, fp);
}

float Noisefv2 (vec2 p)
{
  vec2 t, ip, fp;
  ip = floor (p);  
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = mix (Hashv2v2 (ip), Hashv2v2 (ip + vec2 (0., 1.)), fp.y);
  return mix (t.x, t.y, fp.x);
}

float Fbm2 (vec2 p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    f += a * Noisefv2 (p);
    a *= 0.5;
    p *= 2.;
  }
  return f * (1. / 1.9375);
}

float Fbmn (vec3 p, vec3 n)
{
  vec3 s;
  float a;
  s = vec3 (0.);
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    s += a * vec3 (Noisefv2 (p.yz), Noisefv2 (p.zx), Noisefv2 (p.xy));
    a *= 0.5;
    p *= 2.;
  }
  return dot (s, abs (n));
}

vec3 VaryNf (vec3 p, vec3 n, float f)
{
  vec4 v;
  vec3 g;
  vec2 e = vec2 (0.1, 0.);
  for (int j = VAR_ZERO; j < 4; j ++) {
    v[j] = Fbmn (p + ((j < 2) ? ((j == 0) ? e.xyy : e.yxy) : ((j == 2) ? e.yyx : e.yyy)), n);
  }
  g = v.xyz - v.w;
  return normalize (n + f * (g - n * dot (n, g)));
}

#define txBuf iChannel0
#define txSize iChannelResolution[0].xy

const float txRow = 128.;

vec4 Loadv4 (int idVar)
{
  float fi;
  fi = float (idVar);
  return texture (txBuf, (vec2 (mod (fi, txRow), floor (fi / txRow)) + 0.5) / txSize);
}

