// "Martian Tango" by dr2 - 2021
// License: Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License

float SmoothBump (float lo, float hi, float w, float x);
float Noiseff (float p);
vec4 Loadv4 (int idVar);
void Savev4 (int idVar, vec4 val, inout vec4 fCol, vec2 fCoord);

const int npTrail = 64;  // same for image and buffer
const int npFst = 9;

vec3 carPos, wlBase;
float carRot;
const float pi = 3.1415927;
const float txRow = 128.;

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  vec4 mPtr, mPtrP, stDat;
  vec3 wgBxC, flPos, flVel, flAcc;
  vec2 iFrag, canvas, cnPos, wlRot[2], ud, w, wr[2];
  float tCur, tCurP, autoMd, cRotN, strRot, wlRad, rTurn[2], spd, tc, nStep, asp, s,
     flDir, flDirN, flTilt, pRot, pPitch;
  int pxId, wgSel, nFrame;
  bool init;
  iFrag = floor (fragCoord);
  pxId = int (iFrag.x + txRow * iFrag.y);
  if (pxId >= npFst + npTrail) discard;
  nFrame = iFrame;
  canvas = iResolution.xy;
  tCur = iTime;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  init = (nFrame <= 2);
  asp = canvas.x / canvas.y;
  if (init) {
    carPos = vec3 (0.);
    carRot = 0.;
    strRot = 0.;
    wlBase = vec3 (1.4, 0.5, 2.);
    wlRot[0] = vec2 (0.);
    wlRot[1] = vec2 (0.);
    nStep = 0.;
    cnPos = vec2 (0.);
    mPtrP = mPtr;
    wgSel = 0;
    autoMd = 1.;
    flPos = vec3 (0.);
    flVel = vec3 (0.);
    flDir = 0.5 * pi;
    flTilt = 0.;
    pRot = 0.;
    pPitch = 0.;
  } else {
    stDat = Loadv4 (0);
    mPtrP.xyz = stDat.xyz;
    wgSel = int (stDat.w);
    stDat = Loadv4 (1);
    carPos = stDat.xyz;
    carRot = stDat.w;
    stDat = Loadv4 (2);
    wlBase = stDat.xyz;
    tCurP = stDat.w;
    stDat = Loadv4 (3);
    nStep = stDat.x;
    strRot = stDat.y;
    autoMd = stDat.z;
    stDat = Loadv4 (4);
    wlRot[0] = stDat.xy;
    wlRot[1] = stDat.zw;
    stDat = Loadv4 (5);
    cnPos = stDat.xy;
    stDat = Loadv4 (6);
    flPos = stDat.xyz;
    stDat = Loadv4 (7);
    flVel = stDat.xyz;
    stDat = Loadv4 (8);
    flDir = stDat.x;
    flTilt = stDat.y;
    pRot = stDat.z;
    pPitch = stDat.w;
  }
  if (pxId < npFst) {
    wgBxC = vec3 (0.39, -0.3, 0.135);
    if (mPtr.z > 0.) {
      ud = mPtr.xy * vec2 (asp, 1.) - wgBxC.xy * vec2 (asp, 1.);
      if (wgSel == 1) {
        autoMd = - tCur;
        cnPos = ud;
        s = length (cnPos);
        if (s > 0.) cnPos = min (s, wgBxC.z) * cnPos / s;
      } else if (mPtrP.z <= 0. && length (ud) < wgBxC.z) wgSel = 1;
    } else {
      wgSel = 0;
      cnPos *= 1. - 5e-3;
    }
    wlRad = wlBase.y;
    ++ nStep;
    if (autoMd > 0.) {
      tc = 0.005 * nStep;
      s = 2. * Noiseff (37.1 * mod (floor (tc), 16.)) - 1.;
      s = max (0.3, abs (s)) * sign (s);
      strRot = 0.15 * pi * s * SmoothBump (0.2, 0.8, 0.1, mod (tc, 1.));
      spd = 0.4 * (0.12 - 0.06 * abs (strRot / (0.12 * pi)));
      w = vec2 (- strRot / (0.15 * pi), spd / 0.5);
      w = pow (abs (w), 1. / vec2 (1.5)) * sign (w);
      cnPos = w * wgBxC.z;
    } else {
      w = cnPos / wgBxC.z;
      w = pow (abs (w), vec2 (1.5)) * sign (w);
      strRot = -0.15 * pi * w.x;
      spd = 0.06 * w.y;
      if (tCur + autoMd > 10.) autoMd = 1.;
    }
    wr[0] = vec2 (1.);
    wr[1] = wr[0];
    flAcc = vec3 (carPos.xz - flPos.xz, 0.).xzy;
    if (abs (strRot) > 1e-4) {
      cRotN = carRot - strRot * spd / pi;
      rTurn[0] = wlBase.z / asin (strRot);
      s = wlBase.z / rTurn[0];
      rTurn[1] = rTurn[0] * sqrt (1. - s * s);
      carPos.xz += 2. * rTurn[0] * (sin (carRot - vec2 (0.5 * pi, 0.)) -
         sin (cRotN - vec2 (0.5 * pi, 0.)));
      carRot = mod (cRotN, 2. * pi);
      wr[0] += vec2 (-1., 1.) * wlBase.x / rTurn[0];
      wr[1] += vec2 (-1., 1.) * wlBase.x / rTurn[1];
    } else {
      carPos.xz += spd * sin (carRot + vec2 (0., 0.5 * pi));
    }
    wlRot[0] += wr[0] * spd / wlRad;
    wlRot[1] += wr[1] * spd / wlRad;
    flVel.xz = mix (flVel.xz, flAcc.xz, 0.003);
    flPos.xz += 0.02 * flVel.xz;
    flDirN = (length (flVel.xz) > 0.005) ? atan (- flVel.x, flVel.z) : flDir;
    flDirN -= 2. * pi * sign (flDirN - flDir) * step (pi, abs (flDirN - flDir));
    flDir = mix (flDir, flDirN, 0.01);
    flPos.y = 6. * (1. - smoothstep (4., 10., length (flAcc)));
    flTilt = min (0.03 * pi * length (flVel.xz), 0.1 * pi);
    flTilt *= smoothstep (1., 2., flPos.y);
    pRot += ((flPos.y > 0.05) ? 3. : 0.7) * pi * (tCur - tCurP);
    pPitch = 0.05 * pi * (0.5 + 1.5 * smoothstep (0.03, 0.05, flPos.y));
    flPos.y += 0.5 * Noiseff (tCur);
  }
  if (! init) {
    if (nFrame + 5 < npTrail || mod (float (nFrame), 8.) == 0. &&
       length (carPos.xz - Loadv4 (npFst).xy) > 0.5) {
      if (pxId == npFst) stDat = vec4 (Loadv4 (1).xzw, 0.);
      else if (pxId > npFst && pxId < npFst + npTrail) stDat = Loadv4 (pxId - 1);
    } else if (pxId >= npFst && pxId < npFst + npTrail) stDat = Loadv4 (pxId);
  } else {
    stDat = vec4 (0., 0., -1., 0.);
  }
  if      (pxId == 0) stDat = vec4 (mPtr.xyz, float (wgSel));
  else if (pxId == 1) stDat = vec4 (carPos, carRot);
  else if (pxId == 2) stDat = vec4 (wlBase, tCur);
  else if (pxId == 3) stDat = vec4 (nStep, strRot, autoMd, 0.);
  else if (pxId == 4) stDat = vec4 (wlRot[0], wlRot[1]);
  else if (pxId == 5) stDat = vec4 (cnPos, 0., 0.);
  else if (pxId == 6) stDat = vec4 (flPos, 0.);
  else if (pxId == 7) stDat = vec4 (flVel, 0.);
  else if (pxId == 8) stDat = vec4 (flDir, flTilt, pRot, pPitch);
  Savev4 (pxId, stDat, fragColor, fragCoord);
}

float SmoothBump (float lo, float hi, float w, float x)
{
  return (1. - smoothstep (hi - w, hi + w, x)) * smoothstep (lo - w, lo + w, x);
}

const float cHashM = 43758.54;

vec2 Hashv2f (float p)
{
  return fract (sin (p + vec2 (0., 1.)) * cHashM);
}

float Noiseff (float p)
{
  vec2 t;
  float ip, fp;
  ip = floor (p);
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = Hashv2f (ip);
  return mix (t.x, t.y, fp);
}

#define txBuf iChannel0
#define txSize iChannelResolution[0].xy

vec4 Loadv4 (int idVar)
{
  float fi;
  fi = float (idVar);
  return texture (txBuf, (vec2 (mod (fi, txRow), floor (fi / txRow)) + 0.5) / txSize);
}

void Savev4 (int idVar, vec4 val, inout vec4 fCol, vec2 fCoord)
{
  vec2 d;
  float fi;
  fi = float (idVar);
  d = abs (fCoord - vec2 (mod (fi, txRow), floor (fi / txRow)) - 0.5);
  if (max (d.x, d.y) < 0.5) fCol = val;
}

