#define PI 3.14159265
#define TWO_PI 6.2831853

//------------------------------------------------------------------------
// Distance Funcs
//------------------------------------------------------------------------

float sdPlane( vec3 p )
{
	return p.y;
}
float sdSphere( vec3 p, float s )
{
    return length(p)-s;
}

// https://iquilezles.org/articles/distfunctions
float sdCylinder(vec3 p, vec3 a, vec3 b, float r)
{
    vec3  ba = b - a;
    vec3  pa = p - a;
    float baba = dot(ba,ba);
    float paba = dot(pa,ba);
    float x = length(pa*baba-ba*paba) - r*baba;
    float y = abs(paba-baba*0.5)-baba*0.5;
    float x2 = x*x;
    float y2 = y*y*baba;

    float d = (max(x,y)<0.0)?-min(x2,y2):(((x>0.0)?x2:0.0)+((y>0.0)?y2:0.0));

    return sign(d)*sqrt(abs(d))/baba;
}

float circle(vec2 p, float radius)
{
	return length(p) - radius;
}
float sdTriangleIsosceles( in vec2 p, in vec2 q )
{
    p.x = abs(p.x);
	vec2 a = p - q*clamp( dot(p,q)/dot(q,q), 0.0, 1.0 );
    vec2 b = p - q*vec2( clamp( p.x/q.x, 0.0, 1.0 ), 1.0 );
    float k = sign( q.y );
    float d = min(dot( a, a ),dot(b, b));
    float s = max( k*(p.x*q.y-p.y*q.x),k*(p.y-q.y)  );
	return sqrt(d)*sign(s);
}
float sdOrientedBox( in vec2 p, in vec2 a, in vec2 b, float th )
{
    float l = length(b-a);
    vec2  d = (b-a)/l;
    vec2  q = (p-(a+b)*0.5);
          q = mat2(d.x,-d.y,d.y,d.x)*q;
          q = abs(q)-vec2(l,th)*0.5;
    return length(max(q,0.0)) + min(max(q.x,q.y),0.0);    
}

mat2 rot( float th ){ vec2 a = sin(vec2(1.5707963, 0) + th); return mat2(a, -a.y, a.x); }


// Text
float char(inout vec2 p, int C,float d,float w)
{
    if (p.x<0.|| p.x>1. || p.y<0.|| p.y>1.)
    {
        p.x -= w;
        return d;
    }
    vec4 cc = textureGrad( iChannel0, p/16. + fract( vec2(C, 15-C/16) / 16. ) , dFdx(p/16.),dFdy(p/16.) );
    p.x -= w;
    return min(d,(cc.w-0.505));
}

float fontDist(vec2 p)
{
    p.x *= 2.0;
    p.x += 5.8;
    p.y -= 1.0;
    p.y *= 0.7;

    float d = 1e5;
    float w = 0.514;

    d =  char(p,83,d,w);
    d =  char(p,72,d,w);
    d =  char(p,65,d,w);
    d =  char(p,67,d,w);
    d =  char(p,75,d,w+0.2);
    d =  char(p,78,d,w);
    d =  char(p,69,d,w);
    d =  char(p,87,d,w);
    d =  char(p,83,d,w+5.2);
    p = 1.0-p;
    d =  char(p,50,d,w);
    d =  char(p,53,d,w+0.2);
    d =  char(p,89,d,w);
    d =  char(p,69,d,w);
    d =  char(p,65,d,w);
    d =  char(p,82,d,w);
    d =  char(p,83,d,w);

    return d;
}

float CircleText(vec2 p)
{
	float r = length(p*1.3);
	float a = atan(p.y,p.x);
    float yp = (2.0* PI*(r*r));
    float xp = -a;
    vec2 p2 = vec2(xp, yp);
    return fontDist(p2)+0.01;
}

float DateText(vec2 p)
{
    float d = 1e5;
    float w = 0.5;
    p*=14.0;
    p.y += 0.77;
    p.x -= 4.27;
    d =  char(p,50,d,w);
    d =  char(p,48,d,w);
    d =  char(p,50,d,w);
    d =  char(p,49,d,w-13.1);
    d =  char(p,49,d,w);
    d =  char(p,57,d,w);
    d =  char(p,57,d,w);
    d =  char(p,54,d,w);
    return d;
}

//------------------------------------------------------------------------
// Camera
//------------------------------------------------------------------------
void doCamera( out vec3 camPos, out vec3 camTar, in float time, in vec2 mouse )
{
    float dist = 5.5;
    float vv = sin(iTime*1.1);
    float yoff = 1.5+vv;
    dist += vv*0.5;
    float an = PI*0.5;
	camPos = vec3(dist*sin(an),yoff,dist*cos(an));
    camTar = vec3(0.0,0.0,0.0);
}

vec2 opU( vec2 d1, vec2 d2 )
{
	return (d1.x<d2.x) ? d1 : d2;
}

vec2 doModel( vec3 p )
{
    vec2 res = vec2(0.0,0.0);			// distance,material index

    vec3 pp = p;
    pp.xz*=rot(iTime-1.1f);
    float d1 = sdCylinder(pp,vec3(-0.2,0.0,0.0),vec3(0.2,0.0,0.0),2.0)-0.15;

    p.y += 2.2;
    float d2 = sdPlane(p);

    res = opU(vec2(d1,2.0),vec2(d2,1.0));
    return res;
}

vec3 GetShack(vec2 p)
{
    vec3 background = vec3(0.04);

    //p.y *= 1.2+sin(fract(iTime*0.9)*6.28)*0.1;// bounce
    //p.x *= 1.2+cos(fract(iTime*0.9)*6.28)*0.1;// bounce


    float blur = 0.005;

    //vec3 background = vec3(0.0);
    vec3 grey = vec3(0.2,0.2,0.2)*0.45;
    vec3 txtcol = vec3(0.8,0.8,0.8)*0.45;
    vec3 blue = vec3(0.2,0.6,0.6)*0.45;

    float _d = length(p);
    vec3 color = background;
    float d = smoothstep(0.0,blur, _d-0.3);
    color = mix(grey,color,d);


    d = _d-0.23;
    d = abs(d)-0.01;
    d = smoothstep(0.0,blur, d);
    color = mix(blue,color,d);

    d = _d-0.51;
    d = abs(d)-0.012;
    d = smoothstep(0.0,blur, d);
    color = mix(txtcol,color,d);


	vec2 tri = vec2(0.22,-0.13); // width, height

	float td = sdTriangleIsosceles( p-vec2(-0.05,0.18), tri );
    d = smoothstep(0.0,blur, td-0.0125);
    color = mix(grey,color,d);

    d = smoothstep(0.0,blur, td);
    color = mix(txtcol,color,d);


    vec2 pp = p-vec2(-0.05,0.0);

    d = sdOrientedBox(pp,vec2(-0.09,0.05),vec2(-0.0795,-0.14),0.05);
    d = smoothstep(0.0,blur, d);
    color = mix(txtcol,color,d);

    d = sdOrientedBox(pp,vec2(0.09,0.05),vec2(0.0795,-0.14),0.05);
    d = smoothstep(0.0,blur, d);
    color = mix(txtcol,color,d);

    d = circle(p+vec2(0.07,0.055),0.0125);
    d = smoothstep(0.0,blur, d);
    color = mix(txtcol,color,d);

    d = CircleText(p);
    d = min(d,DateText(p));
    d = smoothstep(0.0,0.03,d);
    if (p.y > 0.1)
        color = mix(txtcol,color,d);
    else
        color = mix(blue,color,d);

    return color;
}

//------------------------------------------------------------------------
// Material 
//
// Defines the material (colors, shading, pattern, texturing) of the model
// at every point based on its position and normal.
//------------------------------------------------------------------------
// c = colour index (added by del for some materials)
vec3 doMaterial( in vec3 pos, in vec3 nor,float c )
{
    if (c<=1.0)
    {
        // checker floor
        float f = mod( floor(1.0*pos.z) + floor(1.0*pos.x), 2.0);
        vec3 col2 = vec3(0.05,0.35,0.45);
        vec3 col1 = col2*0.4;
	    return mix(col1,col2,f);
    }

    mat2 m = rot(iTime-1.1f);
    nor.xz*=m;

    vec3 dir = vec3(1.0,0.0,0.0);
    float dif = dot(nor,dir);

    if (abs(dif)<0.01)
        return vec3(0.04);
    vec3 p = pos;
    p.xz*=m;
    vec2 uv = vec2(p.y,p.z);
    uv *= rot(-PI*0.5);
    uv.x *= sign(dif);
    uv *= 0.27;
    return GetShack(uv);
}

//------------------------------------------------------------------------
// Lighting
//------------------------------------------------------------------------
float calcSoftshadow( in vec3 ro, in vec3 rd );

vec3 doLighting( in vec3 pos, in vec3 nor, in vec3 rd, in float dis, in vec3 mal )
{
    vec3 lin = vec3(0.0);

    // key light
    //-----------------------------
    vec3  lig = normalize(vec3(1.0,0.7,0.9));		// dir
    float dif = max(dot(nor,lig),0.0);
    float sha = 0.0;
    if( dif>0.01 )
        sha=calcSoftshadow( pos+0.01*nor, lig );

    // hack in some specular
    vec3 ref = reflect(rd, nor);
	float spe = max(dot(ref, lig), 0.0);
	dif += pow(spe,16.0);

    lin += dif*vec3(4.00,4.00,4.00)*sha;

    // ambient light
    //-----------------------------
    lin += vec3(0.50,0.50,0.50);

    // surface-light interacion
    //-----------------------------
    vec3 col = mal*lin;

    // fog    
    //-----------------------------
	col *= exp(-0.0075*dis*dis);

    return col;
}

vec2 calcIntersection( in vec3 ro, in vec3 rd )
{
	const float maxd = 40.0;           // max trace distance
	const float precis = 0.001;        // precission of the intersection
    float h = precis*2.0;
    float t = 0.0;
	//float res = -1.0;
    vec2 res = vec2(-1.0,0.0);
    float c = 0.0;

    for( int i=0; i<120; i++ )          // max number of raymarching iterations is 90
    {
        if( h<precis||t>maxd ) break;
        vec2 res2 = doModel( ro+rd*t );
	    h = res2.x;
        c = res2.y;

        t += h;
    }

    if( t<maxd )
    {
        res.x = t;
        res.y = c;
    }
    return res;
}

vec3 calcNormal( in vec3 pos )
{
    const float eps = 0.002;             // precision of the normal computation

    const vec3 v1 = vec3( 1.0,-1.0,-1.0);
    const vec3 v2 = vec3(-1.0,-1.0, 1.0);
    const vec3 v3 = vec3(-1.0, 1.0,-1.0);
    const vec3 v4 = vec3( 1.0, 1.0, 1.0);

	return normalize( v1*doModel( pos + v1*eps ).x + 
					  v2*doModel( pos + v2*eps ).x + 
					  v3*doModel( pos + v3*eps ).x + 
					  v4*doModel( pos + v4*eps ).x );
}

float calcSoftshadow( in vec3 ro, in vec3 rd )
{
    float res = 1.0;
    float t = 0.0005;                 // selfintersection avoidance distance
	float h = 1.0;
    for( int i=0; i<40; i++ )         // 40 is the max numnber of raymarching steps
    {
        h = doModel(ro + rd*t).x;
        res = min( res, 64.0*h/t );   // 64 is the hardness of the shadows
		t += clamp( h, 0.02, 2.0 );   // limit the max and min stepping distances
    }
    return clamp(res,0.0,1.0);
}

mat3 calcLookAtMatrix( in vec3 ro, in vec3 ta, in float roll )
{
    vec3 ww = normalize( ta - ro );
    vec3 uu = normalize( cross(ww,vec3(sin(roll),cos(roll),0.0) ) );
    vec3 vv = normalize( cross(uu,ww));
    return mat3( uu, vv, ww );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = (-iResolution.xy + 2.0*fragCoord.xy)/iResolution.y;
    vec2 m = iMouse.xy/iResolution.xy;

    //-----------------------------------------------------
    // camera
    //-----------------------------------------------------

    // camera movement
    vec3 ro, ta;
    doCamera( ro, ta, iTime, m );

    // camera matrix
    mat3 camMat = calcLookAtMatrix( ro, ta, 0.0 );  // 0.0 is the camera roll

	// create view ray
	vec3 rd = normalize( camMat * vec3(p.xy,2.0) ); // 2.0 is the lens length

    //-----------------------------------------------------
	// render
    //-----------------------------------------------------

  	vec3 col = mix( vec3(0.2, 0.2, 0.5), vec3(0.5, 0.7, 1.0), fragCoord.y / iResolution.y );
    col *= 0.5;

	// raymarch
    vec2 res = calcIntersection( ro, rd ); 
    float t = res.x;
    if( t>-0.5 )
    {
        // geometry
        vec3 pos = ro + t*rd;
        vec3 nor = calcNormal(pos);

        // materials
        vec3 mal = doMaterial( pos, nor, res.y );

        col = doLighting( pos, nor, rd, t, mal );
	}

	//-----------------------------------------------------
	// postprocessing
    //-----------------------------------------------------
    // gamma
	col = pow( clamp(col,0.0,1.0), vec3(0.4545) );

    fragColor = vec4( col, 1.0 );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec2 q=uv-vec2(0.5,0.5);
    q.x*=iResolution.x/iResolution.y;

    float inputVal=fract(iTime);
    //inputVal=inputVal*2.0-1.0;
    inputVal=2.0*sin(iTime);
    float insertVal=(inputVal+2.0)*0.625-0.5;

    float curveVal=1.0-abs(2.0*sin(2.0*atan(q.y,q.x)+inputVal));
    float r=0.3+0.1*insertVal*smoothstep(0.3,1.3,curveVal);
    r*=abs(inputVal);
    float Result =smoothstep(r+0.01,r,length(q));

    // Output to screen
    fragColor = vec4(0,0.7391,1.0,1.0)*Result;
}

