// Distance to an oriented box in the L-infinity metric space. I made 
// a semi-brute force version (checking 3 edges) but before I could 
// take an optimziation pass oneshade wrote a simpler one, which I
// adapted here.
//
// List of some other 2D distances in L-infinity:
//
// https://www.shadertoy.com/playlist/XXccDH


float sdOrientedBox( in vec2 p, in vec2 r, float ang )
{
    vec2 w = vec2(cos(ang),sin(ang));

    vec4  q = w.xyyx * p.xxyy;
    vec4  s = w.xyyx * r.xxyy;

    return max( 
        // rotated rectangle
        max(abs(q.x+q.z)-r.x, abs(q.w-q.y)-r.y ) / 
        max(abs(w.x-w.y),     abs(w.x+w.y)),
        // axis aligned bbox
        max(abs(p.x)-max(abs(s.x-s.z),abs(s.x+s.z)), 
            abs(p.y)-max(abs(s.y+s.w),abs(s.y-s.w)) ) );
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 p = (2.0*fragCoord - iResolution.xy) / iResolution.y;
    vec2 m = (2.0*iMouse.xy - iResolution.xy) / iResolution.y;

    if( iMouse.x<0.01 ) m=sin(-0.17*iTime*vec2(1.1,1.3)+vec2(0,2));

    const vec2 rad = vec2(0.5,0.2);
    float ang = 0.51*iTime;

    float d = sdOrientedBox(p, rad, ang);

    vec3 col = vec3(1.0,0.9,1.0) + sign(d)*vec3(-0.3,0.4,0.3);
	col *= 1.0 - exp(-6.0*abs(d));
	col *= 0.8 + 0.2*cos(150.0*d);
	col = mix( col, vec3(1.0), 1.0-smoothstep(0.0,0.008,abs(d)) );

    d = sdOrientedBox(m, rad, ang);
    col = mix(col, vec3(1.0,1.0,0.0), 1.0-smoothstep(0.004, 0.008, abs(max(abs(p.x-m.x), abs(p.y-m.y)) - abs(d))));
    col = mix(col, vec3(1.0,1.0,0.0), 1.0-smoothstep(0.004, 0.008, length(p-m)-0.015));

	fragColor = vec4(col,1.0);
}

float SUM; 
float TIME;
float T;

void Q(float interval){
  T=clamp((TIME-SUM)/interval,0.,1.);
  SUM=SUM+interval;
}

float apollonian(inout vec3 p)
{
    p=mod(p-2.,4.)-2.;

    float r=12.5;
    float x=5.2;
    float y=7.1;
    float z=2.5;

    SUM=0.;
    TIME = mod(iTime,18.);
    Q(2.);
    r+=T*T*3.;
    Q(2.);
    x-=T*T*5.;
    Q(2.);
    z-=T*T*2.;
    Q(2.);
    y-=T*T*3.;
    Q(2.);
    r+=T*T*2.;
    Q(2.);
    x+=T*T*2.;
    Q(2.);
    y+=T*T*3.;
    Q(2.);
    z+=T*T*4.;

    float e,s=2.;
    for(int i=0;i<6;i++){
        p=abs(p-vec3(x,y,z)*.05)-vec3(x,y,z-.3);
        e=(r+.2)/clamp(dot(p,p),.1,r);
        s*=e;
        p=abs(p)*e;
    }
    return min(length(p.xz),p.y)/s;
}

void mainImage(out vec4 O, vec2 C)
{
    O=vec4(0);
    vec3 p,r=iResolution;
    vec3 ro=eye(iTime);
    vec3 ta =target(iTime);
    vec3 rd=lookat(ro,ta,vec3(0,1,0))*normalize(vec3((C-.5*r.xy)/r.y,1));

    for(float i=1.,g=1.5,e;i<99.;i++)
    {
        p=g*rd+ro;
        g+=e=apollonian(p)+.001;
        O.rgb+=mix(vec3(1),hue(length(p)*.2),.6)*.002/e/i;
    }

    for(float i=1.,g=0.,e;i<99.;i++){
        p=g*rd+ro;
        p=mod(p-.2,1.)-.5;
        g+=e=length(p.xz)-.005;
        e<.001?O.g+=.2/i:i;
    }

    for(float i=1.,g=0.,e;i<99.;i++){
        p=g*rd+ro;
        p=mod(p-.5,1.)-.5;
        g+=e=length(p.yz)-.005;
        e<.001?O.r+=.2/i:i;
    }

    for(float i=1.,g=0.,e;i<99.;i++){
        p=g*rd+ro;

        p=mod(p+.1,1.)-.5;
        g+=e=length(p.xy)-.005;
        e<.001?O.b+=.3/i:i;
    }
    O.xyz = min(O.xyz, .95);
    O.xyz = pow(O.xyz, vec3(.8,1.2,3.));
}

