// Distance to a rhombus in the L-infinity metric space.
//
// List of some other 2D distances in L-infinity:
//
// https://www.shadertoy.com/playlist/XXccDH


float sdRhombus( in vec2 p, float w, float h )
{
    p = abs(p); p.x -= w;

    float f = clamp( (p.y-p.x)/(h+w), 0.0, 1.0 );

    vec2 q = abs(p-f*vec2(-w,h));

    return max(q.x,q.y)*((h*p.x+w*p.y>0.0)?1.0:-1.0);
}

